import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonContent

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import swal from 'sweetalert';
type leveldata = {
    id: string;
    course_name: string;
    level_name: string;

};


const Level: FC = () => {
    const [level, setLevel] = useState([]);
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [ltopic, setLTopic] = useState([]);
    const [topic, setTopic] = useState([]);
    const [level_name, setLevel_name] = useState('');
    const [id, setId] = useState('');
    const fetchlevel = () => {
        fetch(`${baseUrl}${api.allLevel}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setLevel(resp.reverse())
            })
    }
    useEffect(() => {
        fetchlevel();
    }, [])

    const columns = useMemo<MRT_ColumnDef<leveldata>[]>(
        () => [


            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },

            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },

        ],
        [],
    );
    const editLevel = (id: any) => {

        fetch(`${baseUrl}${api.showLevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setLevel_name(result.level.level_name);
                setId(result.level.id);
                setTopic(result.topic);
                setLTopic(

                    result.level.ltopic.map((lt: any) => (lt.topic_name))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateLevel = () => {
        let data = {
            'ltopic': ltopic,
            'level_name': level_name,


        }
        fetch(`${baseUrl}${api.updateLevel}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    fetchlevel();
                    setCollapse(false);
                    swal({
                        title: "Success!",
                        text: "Level updated successfully",
                        icon: "success",
                    });

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchlevel();
                        swal({
                            title: "Success!",
                            text: "Level deleted successfully",
                            icon: "success",
                        });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <Collapse in={collapse}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Level</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Level Name</IonLabel>
                                        <IonInput type="text"
                                            name="level"
                                            value={level_name}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setLevel_name(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Topic</IonLabel>
                                        <IonSelect
                                            name="topic"
                                            value={ltopic}
                                            onIonChange={(e: any) => setLTopic(e.target.value)}

                                            placeholder="Select topic" multiple={true}>
                                            {topic.map((item: any, i: any) => (
                                                < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                            ))}
                                        </IonSelect>

                                    </IonItem>


                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setCollapse(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" onClick={updateLevel}>
                                            Save
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <h1 className='text-center mb-2'>List of Levels</h1>
                <MaterialReactTable
                    columns={columns}
                    data={level}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    positionActionsColumn="last"
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    editLevel(row.id)
                                    setCollapse(!collapse);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="left" title="View" >
                                <IconButton onClick={() => {
                                    editLevel(row.id)
                                    setModalOpen(true);

                                }}>

                                    <Visibility />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteLevel(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />
                <ViewLevel
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    level_name={level_name}
                    level_topic={ltopic}
                />
            </div>
        </IonContent>

    );

};


const ViewLevel: FC<{
    onClose: () => void;

    open: boolean;
    level_name: string;
    level_topic: any

}> = (props) => {

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">View Level</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        //gap: '1.5rem',
                    }}
                >
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Level Name</h2>
                            <p>{props.level_name}</p>
                        </IonLabel>
                    </IonItem>
                    <IonItem >
                        <IonLabel>
                            <h2 className="mb-3">Level Topics</h2>
                            {props.level_topic.map((item: any, i: any) =>
                                <p className="mt-3" key={i}> {i + 1}. {item}</p>
                            )}
                        </IonLabel>
                    </IonItem>


                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>

            </DialogActions>
        </Dialog>
    );
}
export default Level;
