import {
    IonMenu,
    IonHeader,
    IonToolbar,
    IonTitle,
    IonContent,
    IonList,
    IonListHeader,
    IonNote,
    IonMenuToggle,
    IonItem,
    IonLabel,
    IonButton,
    IonButtons,
    IonCol,
    IonCardContent, IonCardHeader, IonCardSubtitle,

    IonInput,

    IonMenuButton,
    IonPage,
    IonRouterOutlet,
    IonRow,
    IonSplitPane,
    IonCard,
    IonCardTitle,
    IonAccordion,
    IonAccordionGroup,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation } from "react-router-dom";
import Course from '../admin/quiz/Course';
import baseUrl, { api } from '../../pages/Urls';
const ViewCourse: React.FC = () => {
    type ViewCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<ViewCourse>();
    const [course_name, setCourse_name] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    useEffect(() => {
        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setQuiz(result.course.quiz)
                setTopic(result.course.topic)
            })

    }, []);

    return (
        <IonCard>
            <IonCardHeader>
                <IonCardTitle>{course_name}</IonCardTitle>
                <IonCardSubtitle>Card Subtitle</IonCardSubtitle>
            </IonCardHeader>

            <IonCardContent>
                <List quiz={quiz} topic={topic} />
            </IonCardContent>
        </IonCard>


    );


}

const List: React.FC<{
    quiz: any[],
    topic: any[]

}> = (props) => {

    return (

        <>

            <IonCard >
                <IonItem >
                    <IonAccordionGroup>
                        <IonAccordion value="first">
                            <IonItem slot="header" color="light">
                                <IonLabel>Quiz</IonLabel>
                            </IonItem>
                            <div className="ion-padding" slot="content">
                                {props.quiz.map((item: any, i: any) =>
                                    <p key={i.toString()}>{item.quiz_name}</p>
                                )}
                            </div>
                        </IonAccordion>
                    </IonAccordionGroup>
                </IonItem >
            </IonCard>

            {props.topic.map((item: any, i: any) =>
                <IonCard key={i.toString()}>
                    <IonItem >
                        <IonAccordionGroup>
                            <IonAccordion value="first">
                                <IonItem slot="header" color="light">
                                    <IonLabel>{item.topic_name}</IonLabel>
                                </IonItem>
                                <div className="ion-padding" slot="content">
                                    Topic Name: {item.id}
                                </div>
                            </IonAccordion>
                        </IonAccordionGroup>
                    </IonItem>
                </IonCard>
            )}

        </>


    );
};
export default ViewCourse;
