import {
    IonContent,
    IonItem,
    IonButton,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams, useLocation } from 'react-router-dom';
import { format } from 'date-fns'
import Countdown from "react-countdown";
import baseUrl, { api } from '../../Urls';
import '../../../pages/Style.css';
import ReactPaginate from "react-paginate";
const QuizQuestion: React.FC<{
    courseId: string,

}> = (props) => {
    type QuizQuestion = {
        id: string;
    };
    const { id } = useParams<QuizQuestion>();
    const history = useHistory();
    const [question, setQuestion] = useState([]);
    const [isAns, setIsAns]: any[] = useState([]);
    const [test_end, setTest_end] = useState(format(new Date(), 'HH:mm:ss'));
    const [test_start, setTest_start] = useState(new Date());
    const [quizId, setQuizId] = useState('');
    const [isAttempt, setIsAttempt] = useState('');
    const [time, setTime] = React.useState(format(new Date(), 'HH:mm:ss'));
    const [pageNumber, setPageNumber] = useState(0);
    const [timer, setTimer] = useState(0);
    const questionPerPage = 3;
    const pagesVisited = pageNumber * questionPerPage;
    const pageCount = Math.ceil(question.length / questionPerPage);

    useEffect(() => {
        const timerID = setInterval(() => {
            tick()
            if (time === format(new Date(test_end), 'HH:mm:ss')) {
                submit()
            }

        }, 1000);
        return function cleanup() {
            clearInterval(timerID);
        };
    });

    function tick() {
        setTime(format(new Date(), 'HH:mm:ss'));

    }

    // useEffect(() => {
    //     return history.listen(() => { // listen
    //         if (history.action === "POP") {
    //             history.replace(`/dashboard/mycourse/${props.courseId}/quiz/${localStorage.getItem("quiz_id")}`);
    //         }
    //     });
    // }, [history]);

    //////

    useEffect(() => {
        setTest_start(new Date())

        fetch(`${baseUrl}${api.viewQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setQuestion(result.question)
                setTest_end(result.question[0].test_end)
                setQuizId(result.question.map((item: any) => (item.quiz_id)))
                setIsAttempt(result.quiz.map((item: any) => (item.is_attempt)))
                localStorage.setItem('quiz_id', result.question[0].quiz_id)
                //history.replace(`/dashboard/mycourse/${props.courseId}/quiz/${localStorage.getItem("quiz_id")}`)
            })


    }, []);


    let attempt = {};
    question.map((q: any, i) => {
        attempt = { ...attempt, [q.id]: isAns[i + pagesVisited] }
    })
    const handleChange = (e: any, i: any) => {
        //const pagesVisited = pageNumber * questionPerPage;
        //console.log(pagesVisited)
        //const index = pagesVisited + i;
        const inputdata = [...isAns]

        inputdata[i + pagesVisited] = e.target.value;
        setIsAns(inputdata)
        console.log(inputdata)

    };
    //console.log(attempt)
    const submit = () => {

        let data = {

            'attemptans': attempt,
            'quiz_id': quizId,

        }

        fetch(`${baseUrl}${api.quizAttempt}`, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(response => {
                if (response.status === 200) {
                    window.confirm('Want to enrole this course?')
                    window.location.href = `/dashboard/mycourse/${props.courseId}/quiz/result`;

                }
                else {
                    throw Error([response.status, response.statusText].join(' '));
                }
            })

            .catch(error => console.log('error', error));




    };


    const changePage = ({ selected: selectedPage }: any) => {
        setPageNumber(selectedPage);
    };

    ///////////////////////////


    var timer2 = new Date(test_end).getTime() - new Date().getTime();
    setTimer(timer2);




    console.log('e', timer);
    //console.log(tes_s);
    return (

        <>

            <IonContent>

                {isAttempt != '1' ?

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>

                                <div className='text-center mt-3'>Total Question -- {question.length} & Quiz will finish {test_end}</div>
                                <Countdown date={timer} />
                                {timer}
                            </IonCardTitle>
                        </IonCardHeader>

                        <IonCardContent>

                            {question.slice(pagesVisited, pagesVisited + questionPerPage)
                                .map((qstn: any, i: any) =>

                                    <IonCard key={i.toString()}>
                                        <IonCardHeader>
                                            <IonCardTitle>
                                                <div className='mb-2'>
                                                    {pagesVisited + i + 1} / {question.length} -- {qstn.title}
                                                </div>


                                                <h2 dangerouslySetInnerHTML={{ __html: qstn.q_content }}></h2>

                                                <>
                                                    {
                                                        (qstn.question_type != 'MCQ') ?
                                                            <>
                                                                <IonItem>
                                                                    <IonInput name='attemptans' value={isAns[pagesVisited + i]} placeholder="enter answer" onIonChange={(e) => handleChange(e, i)}></IonInput>

                                                                </IonItem>
                                                            </> :
                                                            <>
                                                                <IonList>

                                                                    <IonRadioGroup name='attemptans'
                                                                        //selected={isAns[pagesVisited + i]}
                                                                        value={isAns[pagesVisited + i]}
                                                                        onIonChange={(e) => handleChange(e, i)} >
                                                                        {qstn.option.map((op: any, index: any) => {
                                                                            //console.log(isAns[pagesVisited + i])
                                                                            return (

                                                                                <IonItem key={index}>
                                                                                    <IonLabel >{op.option}</IonLabel>
                                                                                    <IonRadio slot="start" value={op.option} ></IonRadio>

                                                                                </IonItem>

                                                                            );
                                                                        })}
                                                                    </IonRadioGroup>
                                                                </IonList>
                                                            </>
                                                    }


                                                </>
                                            </IonCardTitle>
                                        </IonCardHeader>
                                    </IonCard>


                                )}
                            {pageNumber + 1 == pageCount ?
                                <div className='text-center mt-3 mb-5'>
                                    <IonButton id="quiz_submit" fill="outline" onClick={submit}>submit</IonButton>
                                </div> : ''
                            }


                        </IonCardContent>
                        <ReactPaginate
                            previousLabel={"Previous"}
                            nextLabel={"Next"}
                            pageCount={pageCount}
                            onPageChange={changePage}
                            containerClassName={"paginationBttns"}
                            previousLinkClassName={"previousBttn"}
                            nextLinkClassName={"nextBttn"}
                            disabledClassName={"paginationDisabled"}
                            activeClassName={"paginationActive"}
                        />
                    </IonCard>


                    :
                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <div className='text-center mt-3 mb-5'>
                                    You Already Finished your Quiz
                                </div>
                            </IonCardTitle>
                        </IonCardHeader>

                        <IonCardContent>
                            <div className='text-center mt-3 mb-5'>
                                <IonButton fill="outline" href={`/dashboard/mycourse/${props.courseId}`}>Back to Course</IonButton>
                            </div>
                        </IonCardContent>
                    </IonCard>
                }

            </IonContent>
        </>
    );
};


export default QuizQuestion;
