import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonNote,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Collapse } from 'react-bootstrap'
import { useForm, Controller } from "react-hook-form";
const AddCourse: React.FC<{

    collapse: boolean
    instructor: any[]
    onClose: () => void;
    fetchCourse: () => void;

}> = (props) => {


    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState: { errors }
    } = useForm({
        defaultValues: {
            course_name: '',
            description: '',
            crs_inst: [],

        }
    });


    const addCourse = (e: any) => {
        {
            e.preventDefault();



            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {

                        props.onClose();
                        props.fetchCourse();

                    }

                    else {
                        throw new Error("Status code: " + response.status);
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    // console.log(error.course_name)

    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">
                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Create Course</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>
                        <form onSubmit={handleSubmit(onSubmit)}>
                            <IonItem >
                                <IonLabel position="stacked">Course Name</IonLabel>
                                <IonInput
                                    {...register('course_name', {
                                        required: 'course_name is a required field',

                                    })}

                                >
                                </IonInput>
                                <IonNote slot="error"></IonNote>
                            </IonItem>
                            <IonItem>
                                <IonLabel position="stacked">Description</IonLabel>
                                <IonInput
                                    {...register('description')}


                                >
                                </IonInput>
                            </IonItem>
                            <IonItem>
                                <IonLabel>Gender</IonLabel>
                                <Controller
                                    render={({ field }) => (
                                        <IonSelect
                                            placeholder="Select instructors" multiple={true}
                                            value={field.value}
                                            onIonChange={e => setValue('crs_inst', e.detail.value)}
                                        >
                                            {props.instructor.map((item: any, i: any) => (
                                                < IonSelectOption key={i.toString()} value={item.name}>{item.name}</IonSelectOption>
                                            ))}
                                        </IonSelect>
                                    )}
                                    control={control}
                                    name="crs_inst"
                                    rules={{ required: 'This is a required field' }}
                                />
                            </IonItem>

                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="danger" onClick={props.onClose}>
                                    Cancel
                                </IonButton>
                                <IonButton color="primary" onClick={addCourse}>
                                    Save
                                </IonButton>

                            </div>
                        </form>
                    </IonCardContent>
                </IonCard>

            </div>


        </Collapse>
    );
}

export default AddCourse;
