import React, { useState, useEffect, useRef } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = (props) => {
    type ViewQuestionParams = {
        id: string;
    };
    const [questions, setQuestions] = useState([]);
    const [option, setOption]: any[] = useState([]);
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();




    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                setOption(result.map((op: any) => (op.answer)));
            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])
    const getResults = (questions: any, answers: any) => {
        let complete = true;
        let score = 0;

        questions.forEach((question: any) => {
            const givenAnswer = answers[question.id];

            if (givenAnswer === undefined) {
                complete = false;
            }
            if (givenAnswer === question.answer) {
                score++;
            }
        });

        return {
            score,
            complete,
        };
    };

    //console.log('op', option)
    const [answer, setAnswer] = useState([]);
    const [score, setScore] = useState(0);

    const handleSubmit = (evt: any) => {
        evt.preventDefault();

        const { complete, score } = getResults(questions, answer);

        if (complete) {
            setScore(score);
        }
    };

    return (
        <div className='Quiz'>
            <h1 className='Quiz-heading'>Quiz </h1>

            <form onSubmit={handleSubmit}>
                {questions.map((x: any) => {
                    return (
                        <div key={x.id} className='Quiz-question'>
                            <h3>{x.question} </h3>
                            <h3>{x.answer.option} </h3>
                            {/* <div className='Quiz-options'>
                                {x.answer.map((ans: any, idx: any) => (
                                    <div key={idx}>
                                        <input
                                            type='radio'
                                            value={ans}
                                            name={x.id}
                                            checked={answer[x.id] === ans}
                                            onChange={() =>
                                                setAnswer((answer: any) => ({
                                                    ...answer,
                                                    [x.id]: ans,
                                                }))
                                            }
                                        />
                                        <label>{ans}</label>
                                    </div>
                                ))}
                            </div> */}

                        </div>
                    );
                })}
                <button type='submit' className='Quiz-submit'>
                    {' '}
                    Submit{' '}
                </button>
            </form>

            <h1>Score will be shown here</h1>
            <div className='Quiz-output'>
                <h2>
                    {score === 0
                        ? 'Score: For each correct answer you will get 1 point'
                        : 'score: ' + score}
                </h2>
            </div>
        </div>
    );
};

export default ViewQuestion;
