import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';

import React, { useState, useEffect, useRef } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link, useLocation } from 'react-router-dom';
const ViewQuestion: React.FC = (props) => {
    type ViewQuestionParams = {
        id: string;
    };

    const [isAns, setIsAns]: any[] = useState([]);
    const [locationState, setLocationState] = useState({

        question: [],
        mark: '',
        duration: 0,
        limit: 0,
    })
    //const [attempt_ans, setAttempt_ans] = useState('');
    //const time = locationState.duration;
    const [minutes, setMinutes] = useState(1);
    const [seconds, setSeconds] = useState(0);
    const location = useLocation()
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();
    let attempt = {};

    locationState.question.map((q: any, i) => {
        attempt = { ...attempt, [q.id]: isAns[i] }
    })
    useEffect(() => {
        if (location.state) {
            let _state = location.state as any
            setLocationState(_state)
            //window.localStorage.setItem('level', JSON.stringify(_state))

        }
    }, [location])

    // useEffect(() => {

    //     const data = window.localStorage.getItem('level');
    //     if (data !== null) {
    //         setQuizlevel(JSON.parse(data))

    //     };
    // }, [])
    // console.log('level', quizlevel);

    const submit = (e: any) => {

        if (window.confirm("want to submit?")) {

            e.preventDefault();
            let data = {

                'attemptans': attempt,

            }
            fetch(`${baseUrl}${api.attemptQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {

                        window.location.href = `/dashboard/quiz/result/${id}`;

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));


        }
    };
    const handleChange = (e: any, i: any) => {

        const inputdata = [...isAns]
        inputdata[i] = e.target.value;
        setIsAns(inputdata)
        //console.log(e.target.value)

    };


    //console.log('att', attempt)

    // useEffect(() => {

    //     fetch(`${baseUrl}${api.viewQuestion}${id}`, {
    //         method: 'GET',
    //         headers: {
    //             Accept: 'application/json',
    //             'Content-Type': 'application/json',
    //             'Authorization': `Bearer ${localStorage.getItem("token")}`
    //         },
    //     })
    //         .then(res => res.json())
    //         .then(result => {
    //             setQuestions(result);
    //             //setLid(result.map((op: any) => (op.quizlevel_id)))
    //             setTotalQuestion(result.length);

    //         })


    //         .catch(function (error) {
    //             console.log(error);
    //         })


    // }, [])
    //console.log('Q', lid);

    useEffect(() => {
        let myInterval = setInterval(() => {
            if (seconds > 0) {
                setSeconds(seconds - 1);
            }
            if (seconds === 0) {
                if (minutes === 0) {
                    clearInterval(myInterval)
                } else {
                    setMinutes(minutes - 1);
                    setSeconds(59);
                }
            }
        }, 1000)
        return () => {
            clearInterval(myInterval);
        };
    });
    //console.log('op', question_id)
    const count = locationState.limit;
    return (

        <>
            <IonCard>
                <IonCardHeader>
                    <IonCardTitle>
                        <IonGrid>
                            <IonRow>
                                <IonCol>Total Question -- {locationState.limit} Time={locationState.duration}</IonCol>

                                <IonCol>
                                    {minutes === 0 && seconds === 0
                                        ? ''
                                        : <h1> {minutes < 10 ? `0${minutes}` : minutes}:{seconds < 10 ? `0${seconds}` : seconds}</h1>
                                    }
                                </IonCol>
                            </IonRow>
                        </IonGrid>
                    </IonCardTitle>
                </IonCardHeader>

                <IonCardContent>

                    {locationState.question.slice(0, count).map((qstn: any, i: any) =>

                        <IonCard key={i.toString()}>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <div className='mb-2'>
                                        {i + 1} / {locationState.limit}
                                    </div>

                                    {qstn.question}
                                    <h2 dangerouslySetInnerHTML={{ __html: qstn.q_content }}></h2>

                                    <>
                                        {
                                            (qstn.question_type != 'MCQ') ?
                                                <>
                                                    <IonItem>
                                                        <IonInput name='attemptans' value={isAns[i]} placeholder="enter answer" onIonChange={(e) => handleChange(e, i)}></IonInput>

                                                    </IonItem>
                                                </> :
                                                <>
                                                    <IonList>

                                                        <IonRadioGroup name='attemptans' value={isAns[i]} onIonChange={(e) => handleChange(e, i)} >
                                                            {qstn.option.map((op: any, i: any) => {
                                                                return (

                                                                    <IonItem key={i}>
                                                                        <IonLabel >{op.option}</IonLabel>
                                                                        <IonRadio slot="start" value={op.option} ></IonRadio>

                                                                    </IonItem>

                                                                );
                                                            })}
                                                        </IonRadioGroup>
                                                    </IonList>
                                                </>
                                        }


                                    </>
                                </IonCardTitle>
                            </IonCardHeader>
                        </IonCard>


                    )}

                    <div className='text-center mt-3 mb-5'>
                        <IonButton fill="outline" onClick={(e) => submit(e)}>submit</IonButton>
                    </div>
                </IonCardContent>

            </IonCard>



        </>

    );

    // return (
    //     <h1>question</h1>
    // );
}
export default ViewQuestion;
