import {
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardSubtitle, IonLabel,
    IonSegment,
    IonSegmentButton,
    IonCardTitle, IonContent, IonHeader, IonMenu, IonSplitPane, IonTitle, IonToolbar, IonMenuToggle, IonButton, IonItem, IonList, IonRouterOutlet
} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router";

const ViewCourse: React.FC = () => {
    const history = useHistory()
    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }

    return (

        <>
            <IonCard>
                <IonCardHeader>
                    <IonCardTitle>My course name</IonCardTitle>

                    <IonCardSubtitle>
                        <>
                            <IonSegment onIonChange={segmentChangeHandler} value="/dashboard/view/course">


                                <IonSegmentButton value="/dashboard/view/course/topic">Topic</IonSegmentButton>
                                <IonSegmentButton value="/dashboard/course/quiz">Quiz</IonSegmentButton>
                                <IonSegmentButton value="/dashboard/level">Level</IonSegmentButton>
                                <IonSegmentButton value="/dashboard/question">Question</IonSegmentButton>

                            </IonSegment>
                        </>
                    </IonCardSubtitle>
                </IonCardHeader>

                <IonCardContent>

                </IonCardContent>
            </IonCard></>


    );
}
export default ViewCourse;
