import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../pages/Urls';
const Blog: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [title, setTitle] = useState('');
    const [slug, setSlug] = useState('');
    const [content, setContent] = useState('');
    const [status, setStatus] = useState('');
    const [date, setDate] = useState('');
    const [id, setId] = useState('');
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }, [])
    const showModal = (id: any) => {
        setIsOpen(true);

        fetch(`${baseUrl}${api.viewArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.article.title);
                setSlug(result.article.slug);
                setContent(result.article.content);
                setStatus(result.article.status);
                setDate(result.article.date);
                setId(result.article.id)
            })
            .catch(function (error) {
                console.log(error);
            })

    };

    return (
        <>
            <div className="container mt-3">
                <h2>Recent Articles Post</h2>
                <hr />



                <div>

                    {article.map((item: any, i: any) =>
                        <div className="card mb-3">
                            <div className="mt-3 mx-3" >

                                <h4 className="text-primary" onClick={(e) => showModal(item.id)}>{item.title}</h4></div>

                            <>

                                <IonModal isOpen={isOpen} class="fullscreen">

                                    <IonHeader>
                                        <IonToolbar>
                                            <div className="text-center">
                                                <IonTitle >{title}
                                                </IonTitle>
                                            </div>
                                            <IonButtons slot="start">
                                                <IonButton onClick={() => setIsOpen(false)} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                            </IonButtons>
                                        </IonToolbar>
                                    </IonHeader>

                                    <IonContent>
                                        <IonCardContent>
                                            <IonItem >
                                                <h1 color="dark">{title}</h1>

                                            </IonItem>
                                            <IonItem>
                                                <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                                            </IonItem>
                                            <IonItem>
                                                <p>{date}</p>

                                            </IonItem>
                                        </IonCardContent>

                                    </IonContent>

                                </IonModal>

                            </>
                            <div className="mt-2 mx-3" ><p>{item.date}</p></div>

                        </div>
                    )}


                </div>
            </div>

        </>

    );
}
export default Blog;
