import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import baseUrl, { api } from '../../pages/Urls';
import { Link, Route } from 'react-router-dom';
import { useHistory } from "react-router";
import TopicFrom from "./TopicFrom";

const TchrTopic: React.FC<{

    topic: any[]
    course_name: string
    courseId: string

}> = (props) => {

    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [topic_name, setTopic_name] = useState('');
    const [id, setId] = useState('');
    const [search, setSearch] = useState('');
    const [topic, setTopic] = useState([]);
    const fetchTopic = (props: any) => {
        setTopic(props.topic);
    }
    useEffect(() => {
        fetchTopic(props);

    }, [])
    const history = useHistory();
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const deleteTopic = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopic}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        window.location.href = `/dashboard/mycourse/${props.courseId}/topic`;

                    }

                })
                .catch(error => console.log('error', error));
        }
    }

    const editTopic = (id: any) => {

        fetch(`${baseUrl}${api.showTopic}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setTopic_name(result.topic.topic_name);
                setId(result.topic.id)

            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateTopic = () => {
        let data = {
            'topic_name': topic_name,
        }
        fetch(`${baseUrl}${api.updateTopic}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Quiz Successfully');
                    window.location.href = '/dashboard/topic';

                }

            })
            .catch(error => console.log('error', error));


    }
    return (

        <>

            <TopicFrom open={open} handleClose={handleClose} course_name={props.course_name} courseId={props.courseId} />

            <div className="container mt-5">

                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Topic</p>
                    <div className="input-group rounded">
                        <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />

                    </div>
                    <div className="float-end">
                        <button type="button"
                            className="btn btn-light mx-5 my-2"
                            onClick={handleOpen}
                        >
                            <i className="bi bi-plus-circle-fill" title="add-topic" ></i>
                        </button>

                    </div>

                    <TableContainer>

                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>No</TableCell>
                                    <TableCell >Topic Name</TableCell>
                                    <TableCell >Action</TableCell>

                                </TableRow>
                            </TableHead>


                            <TableBody>
                                {(rowsPerPage > 0
                                    ? topic.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : topic
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.topic_name.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell component="th" scope="row">{item.id}</TableCell>
                                        <TableCell >
                                            <Link className='text-decoration-none' to={`/dashboard/mycourse/${props.courseId}/topic/${item.id}`}>
                                                {item.topic_name}
                                            </Link>
                                        </TableCell>

                                        <TableCell >

                                            {/* <button data-bs-toggle="collapse" data-bs-target="#edit">
                                                <i className="bi bi-pencil-square" title="edit-topic"></i>
                                            </button> */}
                                            <button onClick={() => deleteTopic(item.id)} >
                                                <i className="bi bi-trash" title="delete"></i>
                                            </button>
                                        </TableCell>
                                    </TableRow>
                                ))}


                            </TableBody>

                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[5, 10, 20]}
                        component="div"
                        count={topic.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>

            </div>
        </>


    )
}
export default TchrTopic;
