import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonAccordionGroup,
    IonAccordion,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonIcon
} from "@ionic/react";
import { star } from 'ionicons/icons';
import React from "react";
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from "../../pages/Urls";
import { Editor } from '@tinymce/tinymce-react';
class AddArticle extends React.Component<any, any>{
    constructor(props: any) {
        super(props);
        this.state = {
            title: "",
            slug: "",
            content: "",
            status: "",
            date: null,
            redirectToReferrer: false,

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleEditorChange = this.handleEditorChange.bind(this);
        this.addArticle = this.addArticle.bind(this);
    }

    handleChange(e: React.ChangeEvent<any>) {
        this.setState({
            [e.target.name]: e.target.value,


        })
    }

    handleEditorChange(content: any, editor: any) {
        this.setState({ content });
    }


    addArticle(e: React.ChangeEvent<any>) {
        {
            e.preventDefault();

            const { title, slug, content, status, date } = this.state;
            fetch(`${baseUrl}${api.createArticle}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify({
                    title: title,
                    slug: slug,
                    content: content,
                    status: status,
                    date: date
                }),
            })

                .then(response => {
                    if (response.status === 200) {
                        return response.json();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })
                .then(result => {
                    if (result) {
                        this.setState({ redirectToReferrer: true });
                    }

                })
                .catch(error => console.log('error', error));

        }
    }


    render() {
        if (this.state.redirectToReferrer) {
            return (<Redirect to={'/dashboard'} />)
        }
        return (
            <>
                <div className="container mt-5 ">
                    <IonAccordionGroup>
                        <IonAccordion>
                            <IonItem slot="header">
                                <IonLabel>
                                    <div className="float-end">
                                        <IonButton>
                                            Add- Article
                                        </IonButton>
                                    </div>
                                </IonLabel>
                            </IonItem>

                            <div slot="content">
                                <div>
                                    <IonCard>
                                        <IonCardHeader>
                                            <IonCardTitle>
                                                <IonToolbar>
                                                    <IonTitle><h1 className="text-center display-6">Add Article</h1></IonTitle>
                                                </IonToolbar>
                                            </IonCardTitle>
                                        </IonCardHeader>
                                        <hr />
                                        <IonCardContent>
                                            <IonItem>
                                                <IonLabel position="stacked">Title</IonLabel>
                                                <IonInput type="text"
                                                    name="title"
                                                    value={this.state.title}
                                                    required
                                                    clearInput
                                                    onIonChange={(e: any) => this.handleChange(e)}
                                                >
                                                </IonInput>
                                            </IonItem>
                                            <IonItem>
                                                <IonLabel position="stacked">Slug</IonLabel>
                                                <IonInput type="text"
                                                    name="slug"
                                                    value={this.state.slug}
                                                    required
                                                    clearInput
                                                    onIonChange={(e: any) => this.handleChange(e)}
                                                >
                                                </IonInput>
                                            </IonItem>
                                            <IonItem>
                                                <IonLabel position="stacked" >Content</IonLabel>
                                                <div style={{ marginTop: "3%" }}>
                                                    <Editor
                                                        textareaName="content"
                                                        value={this.state.content}
                                                        onEditorChange={this.handleEditorChange}
                                                        init={{
                                                            height: 300,
                                                            width: 1040,
                                                            menubar: true,
                                                            plugins: [

                                                                'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                                'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                                'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                            ],

                                                            toolbar: 'undo redo | blocks | ' +
                                                                'bold italic forecolor | alignleft aligncenter ' +
                                                                'alignright alignjustify | bullist numlist outdent indent | ' +
                                                                'removeformat | help',
                                                            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                        }}
                                                    />
                                                </div>

                                            </IonItem>
                                            <IonItem>
                                                <IonLabel position="stacked">Status</IonLabel>
                                                <IonInput type="text"
                                                    name="status"
                                                    value={this.state.status}
                                                    required
                                                    clearInput
                                                    onIonChange={(e: any) => this.handleChange(e)}
                                                >
                                                </IonInput>
                                            </IonItem>
                                            <IonItem>
                                                <IonLabel position="stacked">Date</IonLabel>
                                                <IonInput type="datetime-local"
                                                    name="date"
                                                    value={this.state.date}
                                                    required
                                                    clearInput
                                                    onIonChange={(e: any) => this.handleChange(e)}
                                                >
                                                </IonInput>
                                            </IonItem>
                                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                                <IonButton color="primary"
                                                    onClick={(e: any) => this.addArticle(e)}>
                                                    Add Article
                                                </IonButton>

                                            </div>
                                        </IonCardContent>
                                    </IonCard>
                                </div>

                            </div>
                        </IonAccordion>
                    </IonAccordionGroup>
                </div>

            </>
        );
    }
}

export default AddArticle;
