import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type Group = {
    id: string;
    name: string;
    email: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const Instructor: FC = () => {
    const [instructor, setInstructor] = useState([]);
    const [groupData, setGroupData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchInstructor = () => {
        fetch(`${baseUrl}${api.allInstructor}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setInstructor(resp.reverse())
            })
    }
    useEffect(() => {
        fetchInstructor();
    }, [])

    const columns = useMemo<MRT_ColumnDef<Group>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
            },
            {
                accessorKey: 'name',
                header: 'Instructor Name',

            },
            {
                accessorKey: 'email',
                header: 'Instructor Email',

            },

        ],
        [],
    );

    const deleteGroup = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteGroup}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        fetchGroup();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Groups</h1>
                <MaterialReactTable
                    columns={columns}
                    data={group}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    setGroupData(row.original)
                                    setModalOpen(true);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteGroup(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-group">
                            <IconButton onClick={() => setModalOpen(true)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />
                <CreateOrEdit
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)
                        setGroupData({})

                    }}
                    fetchGroup={() => fetchGroup()}
                    groupData={groupData}

                />
            </div>
        </IonContent>

    );

};

const CreateOrEdit: FC<{
    onClose: () => void;
    fetchGroup: () => void;
    open: boolean;
    groupData: any
}> = (props) => {
    console.log(props.groupData)
    const type = Object.keys(props.groupData).length == 0 ? "C" : "E";
    const formRef = useRef<HTMLFormElement>(null);
    const {
        register,
        watch,
        reset,
        handleSubmit,
        setValue,
        formState: { errors },
    } = useForm({
        criteriaMode: "all",

    });
    const addGroup = (e: any) => {
        {

            console.log(e)
            // fetch(`${baseUrl}${api.addGroup}`, {
            //     method: 'POST',
            //     headers: {
            //         Accept: 'application/json',
            //         'Content-Type': 'application/json',
            //         'Authorization': `Bearer ${localStorage.getItem("token")}`
            //     },
            //     body: JSON.stringify(e),
            // })

            //     .then(response => {
            //         if (response.status === 200) {
            //             window.alert('Create Group Successfully');
            //             props.fetchGroup();
            //             props.onClose();
            //         }
            //         else {
            //             throw Error([response.status, response.statusText].join(' '));
            //         }
            //     })

            //     .catch(error => console.log('error', error));

        }
    }
    const updateGroup = (e: any) => {

        fetch(`${baseUrl}${api.updateGroup}${e.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Group Successfully');
                    props.fetchGroup();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    const onSubmit = (e: any) => {
        console.log(e)
        type === "C" ? addGroup(e) : updateGroup(e);
    }
    useEffect(() => {

        type === "E" &&
            reset({
                id: props.groupData.id,
                name: props.groupData.name,

            });
    }, [props.groupData]);

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">{type === "C" ? "Create" : "Edit"} Group</DialogTitle>
            <DialogContent>
                <form ref={formRef} onSubmit={handleSubmit(onSubmit)}>
                    <Stack
                        sx={{
                            width: '100%',
                            minWidth: { xs: '300px', sm: '360px', md: '400px' },
                            gap: '1.5rem',
                        }}
                    >

                        <TextField sx={{ mt: 3 }}
                            type="text"
                            label="Group Name"
                            variant="filled"
                            focused
                            {...register("name")}
                        />

                    </Stack>
                </form>
            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={addGroup}>
                    {type === "C" ? "Create" : "Save"}
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default Instructor;
