import React, { FC, useMemo, useState, useEffect } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type Group = {
    id: string;
    name: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const GroupComponent: FC = () => {
    const [group, setGroup] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const fetchGroup = () => {
        fetch(`${baseUrl}${api.allGroup}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setGroup(resp.reverse())
            })
    }
    useEffect(() => {
        fetchGroup();
    }, [])
    //should be memoized or stable
    const columns = useMemo<MRT_ColumnDef<Group>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
            },
            {
                accessorKey: 'name',
                header: 'Name',

            },

        ],
        [],
    );
    const editGroup = (id: any) => {

        fetch(`${baseUrl}${api.editGroup}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setName(result.group.name);
                setId(result.group.id)
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const deleteGroup = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteGroup}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        fetchGroup();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Groups</h1>
                <MaterialReactTable
                    columns={columns}
                    data={group}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => editGroup(row.id)}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteGroup(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-group">
                            <IconButton onClick={() => setModalOpen(true)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />
                <CreateOrEdit
                    open={modalOpen}
                    onClose={() => setModalOpen(false)}
                    gdata={() => fetchGroup()}
                    id={id}
                    name={name}

                />
            </div>
        </IonContent>

    );
};
const CreateOrEdit: FC<{
    onClose: () => void;
    gdata: () => void;
    open: boolean;
    id: string;
    name: string;
}> = (props) => {
    const [name, setName] = useState('');
    const type = props.id === undefined ? "C" : "E";
    const {
        register,
        watch,
        reset,
        handleSubmit,
        setValue,
        formState: { errors },
    } = useForm({
        criteriaMode: "all",

    });
    const addGroup = (e: any) => {
        {

            let data = {
                'name': name,
            }
            fetch(`${baseUrl}${api.addGroup}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Group Successfully');
                        props.gdata();
                        props.onClose();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    const updateGroup = (e: any) => {
        let data = {
            'name': name,
        }
        fetch(`${baseUrl}${api.updateGroup}${props.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Group Successfully');
                    props.gdata();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    const onSubmit = (e: any) => {

        type === "C" ? addGroup(e) : updateGroup(e);
    }
    useEffect(() => {

        type === "E" &&
            reset({
                id: props.id,
                name: props.name,

            });
    }, []);
    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">{type === "C" ? "Create" : "Edit"}Group</DialogTitle>
            <DialogContent>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <Stack
                        sx={{
                            width: '100%',
                            minWidth: { xs: '300px', sm: '360px', md: '400px' },
                            gap: '1.5rem',
                        }}
                    >

                        <TextField sx={{ mt: 3 }}
                            type="text"
                            label="Group Name"
                            variant="filled"
                            focused
                            {...register("name")}
                            onChange={(e: any) => setName(e.target.value)} />

                    </Stack>
                </form>
            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={addGroup}>
                    Add
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default GroupComponent;
