import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from '../../../pages/Urls';


const Course: React.FC = () => {

    const [course, setCourse] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [course_name, setCourse_name] = useState('');
    const [id, setId] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const [course_id, setCourse_id] = useState('');
    const [topic_name, setTopic_name] = useState('');


    useEffect(() => {
        fetch(`${baseUrl}${api.allCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }, [])


    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - course.length) : 0;

    const addCourse = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'course_name': course_name,
            }
            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Course Successfully');
                        window.location.href = '/dashboard/course';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    const editCourse = (id: any) => {

        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setCourse_name(result.course.course_name);
                setId(result.course.id)
            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const updateCourse = () => {
        let data = {
            'course_name': course_name,
        }
        fetch(`${baseUrl}${api.updateCourse}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Course Successfully');
                    window.location.href = '/dashboard/course';

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteCourse = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteCourse}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        setRedirectToReferrer(true);
                    }

                })
                .catch(error => console.log('error', error));
        }
    }

    const addTopic = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'course_id': course_id,
                'topic_name': topic_name,

            }
            fetch(`${baseUrl}${api.addTopic}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Quiz level Successfully');
                        window.location.href = '/dashboard/course';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <div className="container mt-5">

            <>
                <IonModal isOpen={isOpen}>
                    <IonHeader>
                        <IonToolbar>
                            <IonTitle>Add-Quiz</IonTitle>
                            <IonButtons slot="end">
                                <IonButton onClick={() => setIsOpen(false)}>Close</IonButton>
                            </IonButtons>
                        </IonToolbar>
                    </IonHeader>
                    <IonContent className="ion-padding">
                        <IonItem>
                            <IonLabel position="stacked">Course Name</IonLabel>
                            <IonInput
                                placeholder="course name..."
                                onIonChange={(e: any) => setCourse_name(e.target.value)}
                                clearInput></IonInput>
                        </IonItem>
                    </IonContent>
                    <IonContent>
                        <div className="text-center">
                            <IonButton onClick={addCourse}>
                                Add-Course
                            </IonButton>
                        </div>
                    </IonContent>
                </IonModal>
            </>

            <div id="demo" className="collapse mt-3 mb-3">

                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Course</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Quiz Name</IonLabel>
                                    <IonInput type="text"
                                        name="quiz_name"
                                        value={course_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setCourse_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={updateCourse}>
                                        Update Course
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>
            <div id="add" className="collapse mt-3 mb-3">

                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">add Topic</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Course Id</IonLabel>
                                    <IonInput type="text"
                                        name="id"
                                        value={id}
                                        disabled={true}
                                        onIonChange={(e: any) => setCourse_id(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Topic Name</IonLabel>
                                    <IonInput type="text"
                                        name="topic_name"
                                        placeholder="topic name..."
                                        onIonChange={(e: any) => setTopic_name(e.target.value)}
                                        clearInput></IonInput>
                                </IonItem>


                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={addTopic}>
                                        Add Quiz Level
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>

            <Paper sx={{ width: '100%', mb: 2 }}>
                <p className="h1 text-center">List of Courses</p>
                <div className="float-end">
                    <button type="button"
                        className="btn btn-light mx-5 my-2"
                        onClick={() => setIsOpen(true)}
                    >
                        <i className="bi bi-plus-circle-fill" title="add-course"></i>
                    </button>

                </div>
                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Id</TableCell>
                                <TableCell >Course Name</TableCell>
                                <TableCell >Action</TableCell>

                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {(rowsPerPage > 0
                                ? course.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                : course
                            ).map((item: any, i: any) => (
                                <TableRow key={i.toString()}>
                                    <TableCell component="th" scope="row">{item.id}</TableCell>
                                    <TableCell >{item.course_name}</TableCell>

                                    <TableCell >
                                        <button onClick={() => addTopic(item.id)} data-bs-toggle="collapse" data-bs-target="#add">
                                            <i className="bi bi-plus-circle-dotted" title="add-topic"></i>
                                        </button>
                                        <button onClick={() => editCourse(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                            <i className="bi bi-pencil-square" title="edit-course"></i>
                                        </button>

                                        <button onClick={() => deleteCourse(item.id)} >
                                            <i className="bi bi-trash" title="delete"></i>
                                        </button>
                                    </TableCell>
                                </TableRow>
                            ))}


                        </TableBody>
                    </Table>

                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[1, 2, 5]}
                    component="div"
                    count={course.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>

        </div>
    );
}

export default Course;
