import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import baseUrl, { api } from '../../pages/Urls';
import { Link, Route } from 'react-router-dom';
import { useHistory } from "react-router";


const TchrQuestion: React.FC<{
    question: any[]
    courseId: string

}> = (props) => {

    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [id, setId] = useState('');
    const history = useHistory();
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const deleteQuestion = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuestion}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        window.location.href = `/dashboard/mycourse/${props.courseId}/question`;

                    }

                })
                .catch(error => console.log('error', error));
        }
    }

    return (
        <>


            <div className="container mt-5">

                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Question</p>
                    <div className="float-end mx-5">
                        <Link className='text-decoration-none' to={`/dashboard/mycourse/${props.courseId}/question/add`}>
                            <i className="bi bi-plus-circle-fill" title="add-question" ></i>
                        </Link>


                    </div>

                    <TableContainer>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Id</TableCell>
                                    <TableCell >Question</TableCell>
                                    <TableCell >Question Type</TableCell>
                                    <TableCell >Question Answer</TableCell>
                                    <TableCell >Action</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(rowsPerPage > 0
                                    ? props.question.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : props.question
                                ).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell component="th" scope="row">{item.id}</TableCell>

                                        <TableCell >{item.title}</TableCell>
                                        <TableCell >{item.question_type}</TableCell>
                                        <TableCell >{item.answer}</TableCell>
                                        <TableCell >
                                            <button >
                                                <i className="bi bi-pencil-square" title="edit-question"></i>
                                            </button>

                                            <button onClick={() => deleteQuestion(item.id)} >
                                                <i className="bi bi-trash" title="delete"></i>
                                            </button>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[5, 10, 20]}
                        component="div"
                        count={props.question.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>

            </div>

        </>

    )
}
export default TchrQuestion;
