import {
    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import baseUrl, { api } from '../../../pages/Urls';

const ViewBlog: React.FC = () => {
    type ViewBlogParams = {
        id: string;
    };
    const [title, setTitle] = useState('');
    //const [id, setId] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const history = useHistory();
    const params = useParams()
    const { id } = useParams<ViewBlogParams>();
    const back = () => {
        history.push("/dashboard/blog")
    }
    useEffect(() => {
        console.log(params)
        fetch(`${baseUrl}${api.viewArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
            })
            .catch(function (error) {
                console.log(error);
            })

    }, [params])
    return (
        <>

            <IonCard>

                <IonCardHeader>
                    <IonToolbar>
                        <div className="text-center">
                            <IonTitle >
                                {title}
                            </IonTitle>
                        </div>
                        <IonButtons slot="start">
                            <IonButton onClick={back} > <i className="bi bi-backspace"> Back</i> </IonButton>
                        </IonButtons>
                    </IonToolbar>
                </IonCardHeader>


                <IonCardContent>

                    <h1 color="dark">{title}</h1>


                    <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>

                    <p>{date}</p>


                </IonCardContent>



            </IonCard>

        </>

    );
};
export default ViewBlog;
