import {
    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,
    IonItem,

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
const ViewTopicArticle: React.FC = () => {
    const location = useLocation()
    const history = useHistory();
    const [locationState, setLocationState] = useState({

        artname: '',
        artcontent: '',
        course_id: '',

    })

    useEffect(() => {
        if (location.state) {
            let _state = location.state as any

            localStorage.setItem('topicArticle', JSON.stringify(_state))

        }
    }, [location])

    useEffect(() => {

        const data = localStorage.getItem('topicArticle');
        if (data !== null) {
            setLocationState(JSON.parse(data))

        };
    }, [])
    const back = () => {
        history.push(`/dashboard/mycourse/${locationState.course_id}`)
    }

    return (

        <>

            <IonCard>

                <IonCardHeader>
                    <IonToolbar>
                        <div className="text-center">
                            <IonTitle >
                                {locationState.artname}
                            </IonTitle>
                        </div>
                        <IonButtons slot="start">
                            <IonButton onClick={back} > <i className="bi bi-backspace"> Back</i> </IonButton>
                        </IonButtons>
                    </IonToolbar>
                </IonCardHeader>


                <IonCardContent>
                    <IonItem>
                        <h1 color="dark">{locationState.artname}</h1>

                    </IonItem>
                    <IonItem>
                        <h2 dangerouslySetInnerHTML={{ __html: locationState.artcontent }}></h2>
                    </IonItem>

                </IonCardContent>



            </IonCard>

        </>
    )
}
export default ViewTopicArticle;
