import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../pages/Urls';
const AddLevel: React.FC<{

    topic: any[]
    course_name: string
    courseId: string

}> = (props) => {

    const [level_name, setLevel_name] = useState('');
    const [topic_name, setTopic_name] = useState('');
    const [error, setError] = useState("");

    const addLevel = (e: any) => {
        {
            let data = {
                'course_name': props.course_name,
                'topic_name': topic_name,
                'level_name': level_name,

            }
            fetch(`${baseUrl}${api.addLevel}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Quiz Successfully');
                        window.location.href = `/dashboard/mycourse/${props.courseId}/level`;

                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <>
            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">add Level</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>

                        <IonItem>
                            <IonLabel position="stacked">Level Name</IonLabel>
                            <IonInput type="text"
                                name="quiz_name"
                                placeholder="quiz name..."
                                onIonChange={(e: any) => setLevel_name(e.target.value)}
                                clearInput></IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Topic</IonLabel>
                            <IonSelect
                                name="topic_name"
                                value={topic_name}
                                onIonChange={(e: any) => setTopic_name(e.target.value)}

                                placeholder="Select topic" >
                                {props.topic.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                ))}
                            </IonSelect>

                        </IonItem>


                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="primary" onClick={addLevel}>
                                Add Level
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>

        </>
    )
}
export default AddLevel;
