import React, { FC, useMemo, useState, useEffect } from 'react';
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { IonContent } from '@ionic/react';
type Group = {
    id: string;
    name: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const GroupComponent: FC = () => {
    const [group, setGroup] = useState([]);
    const fetchGroup = () => {
        fetch(`${baseUrl}${api.allGroup}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setGroup(resp.reverse())
            })
    }
    useEffect(() => {
        fetchGroup();
    }, [])
    //should be memoized or stable
    const columns = useMemo<MRT_ColumnDef<Group>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
            },
            {
                accessorKey: 'name',
                header: 'Name',

            },

        ],
        [],
    );
    const deleteGroup = (id: any, row: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteGroup}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        group.splice(row.index, 1)
                        setGroup([...group])
                    }
                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Groups</h1>
                <MaterialReactTable
                    columns={columns}
                    data={group}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton >
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteGroup(row.id, row)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-group">
                            <IconButton >
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />
            </div>
        </IonContent>

    );
};

export default GroupComponent;
