import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,

} from "@ionic/react";
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    FormControl,
    NativeSelect,
    Stack,
    Tooltip,
    InputLabel,
} from '@mui/material';
import Grid from '@material-ui/core/Grid';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/material/styles';
import baseUrl, { api } from '../../../pages/Urls';

interface FileThumbProps {
    abs_url: string,
    rel_url: string,
};

interface FileThumbsProps {
    large: FileThumbProps,
    medium: FileThumbProps,
    small: FileThumbProps,
};

interface FileProps {
    abs_url: string,
    last_modified: number,
    mime: string,
    name: string,
    rel_url: string,
    size: number,
    thumbs: FileThumbsProps,
}

interface ImageViewerProps {
    file: FileProps,
};

const ImageViewer: FC<ImageViewerProps> = (props: ImageViewerProps) => { 
    const [file, setFile] = useState<FileProps|null>(null);

    useEffect(() => {
        setFile(props.file);
    }, [props.file]);

    return (
        <>

        </>
    );
};

const Media: FC = () => {
    const [files, setFiles] = useState<FileProps[]>([]);
    const [modalOpen, setModalOpen] = useState(false);

    const fetchData = () => {
        fetch(`${baseUrl}${api.media}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(res => {
                setFiles(res)
            });
    };

    useEffect(() => {
        fetchData();
    }, []);

    const Item = styled(Paper)(({ theme }) => ({
        backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
        ...theme.typography.body2,
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    }));


    console.log('files', files);

    return (
        <>
            <IonContent>
                <div className="container mt-5">
                    <h1 className='text-center mb-2'>Media Gallery</h1>

                    <Box sx={{ flexGrow: 1 }}>
                        <Grid container spacing={2}>

                            {files.map((file, i) => <Grid key={`grid-file-${i}`} item xs={3}>
                                <Item>
                                    <div>
                                        {file.thumbs ? <img src={file.thumbs.medium.abs_url} /> : null}       
                                    </div>
                                    <div>
                                        <b>{file.name}</b>
                                    </div>
                                    <div>
                                        <i>{file.mime}</i>
                                    </div>
                                </Item>
                            </Grid>)}
                        </Grid>
                    </Box>
                </div>
                <ImageViewer
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false);
                    }}
                />
            </IonContent >
        </>
    );
};

export default Media;
