import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonSearchbar,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import { format } from "date-fns";
import swal from 'sweetalert';
import { useForm, Controller } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import Select from "react-select";

const schema = yup.object().shape({
    slug: yup.string()
        .required('Slug is required'),

    title: yup.string()
        .required('Tilte is required'),
    content: yup.string()
        .required('Content is required'),
    status: yup.string()
        .required('Status is required'),
    date: yup.string()
        .required('Date is required'),
})
const AddArticle: React.FC<{

    collapse: boolean
    group: any[]
    category: any[]
    onClose: () => void;
    fetchArticle: () => void;

}> = (props) => {
    const [error, setError] = useState('');
    const [selectedOptions, setSelectedOptions] = useState();
    const [Options, setOptions] = useState([{}]);
    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
        watch,
        getValues
    } = useForm({
        defaultValues: {
            title: '',
            slug: '',
            content: '',
            status: '',
            category: [],
            gname: [],
            date: format(new Date(), "yyyy-MM-dd"),

        },
        resolver: yupResolver(schema),
        //mode: "all"
    });

    const handleChange = (e: any) => {

        const slg = e.target.value;

        if (slg) {
            fetch(`${baseUrl}${api.checkSlug}${slg}`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
            })
                .then(resp => resp.json())
                .then(res => {
                    if (Object.keys(res).length != 0) {
                        setError('Slug already exists')
                    }
                })
        }

    }

    const makeSlug = (e: any) => {
        let data = {
            'title': e.target.value,
        }
        fetch(`${baseUrl}${api.createSlug}`, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        }).then(res => res.json())
            .then((res) => {
                setValue('slug', res)
            })

    }
    const addArticle = (e: any) => {
        {


            fetch(`${baseUrl}${api.createArticle}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchArticle();
                        props.onClose();
                        reset({
                            title: '',
                            slug: '',
                            content: '',
                            status: '',
                            gname: [],
                            category: [],

                        })
                        swal({
                            title: "Success!",
                            text: "Article Created successfully",
                            icon: "success",
                        });
                    }


                })

                .catch(errors => console.log(errors.response.data.errors));
        }
    }

    function handleSelect(data: any) {
        setSelectedOptions(data);
    }
    useEffect(() => {
        const groupData = props.group.map((item) => ({
            label: item.name,
            value: item.id
        }));
        setOptions(groupData);
    }, [props.group]);
    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">


                <form onSubmit={handleSubmit(addArticle)}>
                    <IonItem>
                        <IonLabel position="stacked">Title</IonLabel>
                        <IonInput placeholder="Title"
                            type="text"
                            {...register("title")}
                            //onIonChange={(e: any) => setTitle(e.target.value)}
                            onBlur={(e: any) => {
                                ///setTitle(e.target.value)
                                setValue('title', e.target.value)
                                makeSlug(e)
                            }}
                        >
                        </IonInput>
                    </IonItem>
                    <p style={{ color: "red" }}>
                        <ErrorMessage errors={errors} name="title" />
                    </p>
                    <IonItem>
                        <IonLabel position="stacked">Slug</IonLabel>
                        <IonInput placeholder="slug"
                            type="text"

                            {...register("slug")}
                            onIonChange={(e: any) => handleChange(e)}
                        >
                        </IonInput>
                    </IonItem>
                    <p style={{ color: "red" }}>
                        <ErrorMessage errors={errors} name="slug" />
                    </p>
                    <p style={{ color: "red" }}>
                        {error ? error : ''}
                    </p>
                    <IonItem>
                        <IonLabel position="stacked" >Content</IonLabel>
                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                            <Controller
                                name="content"
                                control={control}

                                render={({ field: { ...rest } }) => (
                                    <Editor
                                        textareaName="content"
                                        value={rest.value}

                                        init={{
                                            height: 500,
                                            width: 1030,
                                            menubar: true,
                                            plugins: [

                                                'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                            ],

                                            toolbar: 'undo redo | blocks | ' +
                                                'bold italic forecolor | alignleft aligncenter ' +
                                                'alignright alignjustify | bullist numlist outdent indent | ' +
                                                'removeformat | help',
                                            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'

                                        }}
                                        onEditorChange={(e) => {
                                            setValue('content', e)
                                        }}
                                    />

                                )}
                            />
                        </div>

                    </IonItem>
                    <p style={{ color: "red" }}>
                        <ErrorMessage errors={errors} name="content" />
                    </p>
                    <IonItem>
                        <IonLabel position="stacked">Category</IonLabel>

                        <IonSelect
                            {...register("category")}

                            placeholder="Select category" multiple={true}>

                            {props.category.map((item: any, i: any) => (
                                < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                            ))}
                        </IonSelect>

                    </IonItem>


                    <h2>Group</h2>
                    <div className="dropdown-container">
                        <Select
                            options={Options}
                            placeholder="Select color"
                            value={selectedOptions}
                            onChange={handleSelect}
                            isSearchable={true}
                            isMulti
                        />
                    </div>




                    {/* <IonItem>
                                <IonLabel position="stacked">Group</IonLabel>

                                <IonSelect
                                    {...register("gname")}

                                    placeholder="Select groups" multiple={true}>

                                    {props.group.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem> */}
                    <IonItem>
                        <IonLabel position="stacked">Status</IonLabel>

                        <IonSelect
                            placeholder="status"

                            {...register("status")}
                        >
                            {(localStorage.getItem('role') == 'ar_staff1') ?
                                <IonSelectOption value='R'>Review</IonSelectOption> :
                                <>
                                    <IonSelectOption value='P'>Publish</IonSelectOption>
                                    <IonSelectOption value='D'>Draft</IonSelectOption>

                                </>



                            }
                        </IonSelect>

                    </IonItem>
                    <p style={{ color: "red" }}>
                        <ErrorMessage errors={errors} name="status" />
                    </p>

                    <IonItem>
                        <IonLabel position="stacked">Date</IonLabel>

                        <Controller
                            name="date"
                            control={control}

                            defaultValue={format(new Date(), "yyyy-MM-dd")}
                            render={({ field: { ...rest } }) => (
                                <LocalizationProvider dateAdapter={AdapterDateFns}>
                                    <DesktopDatePicker

                                        inputFormat="yyyy-MM-dd"

                                        value={rest.value}
                                        onChange={(e: any) => {
                                            setValue("date", format(e, "yyyy-MM-dd"));

                                        }}
                                        renderInput={(params) => (
                                            <TextField {...params} sx={{ mt: 1, width: '100%' }} />
                                        )}
                                    />
                                </LocalizationProvider>
                            )}
                        />

                    </IonItem>

                    <p style={{ color: "red" }}>
                        <ErrorMessage errors={errors} name="date" />
                    </p>
                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                        <IonButton color="danger" onClick={props.onClose}>
                            Cancel
                        </IonButton>
                        <IonButton color="primary" type="submit">
                            Create
                        </IonButton>

                    </div>
                </form>


            </div>


        </Collapse >
    );
}

export default AddArticle;
