import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import { Link } from 'react-router-dom';
import { useHistory } from "react-router";
import React, { useState, useEffect } from "react";
const HeaderButton: React.FC = () => {
    const [name, setName] = useState('');
    const history = useHistory();
    const logout = () => {
        localStorage.removeItem("token");

    };
    useEffect(() => {

    }, []);

    return (


        <IonButtons slot="secondary">

            <IonButton id="profile-trigger">
                <IonIcon slot="icon-only" icon={personCircle}></IonIcon>
            </IonButton>
            <IonPopover
                trigger="profile-trigger"
                triggerAction="click"
                showBackdrop={false}
            >
                <IonContent>
                    <IonItem>
                        <IonIcon slot="end" icon={personCircle}></IonIcon>
                        <IonLabel>`{localStorage.getItem("username")}`</IonLabel>
                    </IonItem>
                    <IonItem routerLink="/dashboard" routerDirection="none">
                        <IonIcon slot="end" icon={settingsOutline}></IonIcon>
                        <IonLabel>Dashboard</IonLabel>
                    </IonItem>
                    <IonItem href="" onClick={logout} >
                        <IonIcon slot="end" icon={exitOutline}></IonIcon>
                        <IonLabel>Logout</IonLabel>
                    </IonItem>
                </IonContent>
            </IonPopover>

        </IonButtons>
    );
};

export default HeaderButton;
