import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonNavLink,
    IonRouterLink,
    IonRouterOutlet,

} from '@ionic/react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory } from "react-router";
import { Link, Route } from 'react-router-dom';
import ViewQuiz from './ViewQuiz';
const Uquiz: React.FC = () => {

    const [quiz, setQuiz] = useState([]);
    const [quiz_name, setQuiz_name] = useState('');
    const [quiz_id, setQuiz_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const history = useHistory();
    useEffect(() => {
        fetch(`${baseUrl}${api.quiz}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuiz(resp.reverse())
            })
    }, [])

    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - quiz.length) : 0;

    return (
        <>

            <Route path="/dashboard/quiz/:id" component={ViewQuiz} />

            <div className="container mt-3">
                <h2>Quiz List</h2>
                <hr />

                <Paper sx={{ width: '100%', mb: 2, mt: 3 }}>

                    <TableContainer>
                        <Table aria-label="simple table">

                            <TableBody>
                                {(rowsPerPage > 0
                                    ? quiz.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : quiz
                                ).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell >
                                            <div>
                                                <h4 className="text-primary"><Link className='text-decoration-none' to={`/dashboard/quiz/${item.id}`}>{item.quiz_name}</Link></h4>
                                            </div>

                                        </TableCell>


                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[1, 2, 7]}
                        component="div"
                        count={quiz.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />

                </Paper>
            </div>

        </>

    );
}
export default Uquiz;
