import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from '../../../pages/Urls';


const Quiz: React.FC = () => {

    const [quiz, setQuiz] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [quiz_name, setQuiz_name] = useState('');
    const [id, setId] = useState('');
    const [status, setStatus] = useState('');
    const [date, setDate] = useState('');
    const [search, setSearch] = useState('');
    const [level, setLevel] = useState([]);


    /////
    const [inputList, setinputList] = useState([{ level_name: '', qcount: 0 }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setinputList(list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { level_name: '', qcount: 0 }]

        setinputList(abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setinputList(list);
    }
    /////
    useEffect(() => {
        fetch(`${baseUrl}${api.allQuiz}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuiz(resp.reverse())
            })
    }, []);

    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - quiz.length) : 0;


    const editQuiz = (id: any) => {

        fetch(`${baseUrl}${api.showQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setQuiz_name(result.quiz.quiz_name);
                setStatus(result.quiz.status);
                setDate(result.quiz.date);
                setLevel(result.level);
                setId(result.quiz.id)
                let levelList: any[] = [];
                {
                    result.quiz.qlevel.map((item: any, i: any) =>
                        levelList.push({
                            level_name: item.level_name,
                            qcount: item.qcount
                        })
                    )
                }
                setinputList([...levelList]);

            })
            .catch(function (error) {
                console.log(error);
            })


    }
    //console.log('test', inputList);
    const updateQuiz = () => {
        let data = {
            'quiz_name': quiz_name,
            'status': status,
            'date': date,
            'inputlist': inputList,
        }
        fetch(`${baseUrl}${api.updateQuiz}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Quiz Successfully');
                    window.location.href = '/dashboard/quiz';

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuiz = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuiz}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        window.location.href = '/dashboard/quiz';
                    }

                })
                .catch(error => console.log('error', error));
        }
    }


    return (
        <IonContent>
            <div className="container mt-5">

                <div id="demo" className="collapse mt-3 mb-3">

                    <>
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Quiz</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Quiz Name</IonLabel>
                                        <IonInput type="text"
                                            name="quiz_name"
                                            value={quiz_name}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setQuiz_name(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonText color="primary">
                                            <h2 className="mt-3 mb-3">Add Quiz Level</h2>
                                        </IonText>
                                        <button onClick={() => handleaddclick()} className="mx-3 ">
                                            <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                        </button>
                                    </IonItem>
                                    <div>

                                        {
                                            inputList.map((x, i) => {
                                                //console.log('qq', x.qcount)
                                                return (
                                                    <div key={i.toString()} className="row mb-3 mt-3">
                                                        <div className=" col-md-4">
                                                            <IonItem>
                                                                <IonLabel position="stacked">Level</IonLabel>
                                                                <IonSelect
                                                                    name="level_name"
                                                                    value={x.level_name}
                                                                    onIonChange={(e: any) => handleinputchange(e, i)}

                                                                >
                                                                    {level.map((item: any, index: any) => (
                                                                        < IonSelectOption key={index.toString()}>{item.level_name}</IonSelectOption>
                                                                    ))}
                                                                </IonSelect>
                                                            </IonItem>
                                                        </div>
                                                        <div className=" col-md-4">
                                                            <IonItem>
                                                                <IonLabel position="stacked">No of Question</IonLabel>
                                                                <IonInput type="number"
                                                                    name="qcount"
                                                                    value={x.qcount}
                                                                    required
                                                                    clearInput
                                                                    onIonChange={(e: any) => handleinputchange(e, i)}
                                                                >
                                                                </IonInput>
                                                            </IonItem>
                                                        </div>
                                                        <div className="col-md-4 mt-4">
                                                            <button onClick={() => handleremove(i)}>X</button>
                                                        </div>



                                                    </div>
                                                );
                                            })}

                                    </div>


                                    <IonItem>
                                        <IonLabel position="stacked">Status</IonLabel>

                                        <IonSelect
                                            name="status"
                                            value={status}
                                            onIonChange={(e: any) => setStatus(e.target.value)}
                                        >

                                            <>
                                                <IonSelectOption>P</IonSelectOption>
                                                <IonSelectOption>D</IonSelectOption>

                                            </>

                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Date</IonLabel>

                                        <IonInput type="date"
                                            name="date"
                                            value={date}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setDate(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>

                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="primary" onClick={updateQuiz}>
                                            Update Quiz
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </>

                </div>


                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Quizzes</p>
                    <div className="input-group rounded">
                        <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />

                    </div>
                    <TableContainer>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Id</TableCell>
                                    <TableCell >Course Name</TableCell>
                                    <TableCell >Quiz Name</TableCell>
                                    <TableCell >Status</TableCell>
                                    <TableCell >Action</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(rowsPerPage > 0
                                    ? quiz.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : quiz
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.quiz_name.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell component="th" scope="row">{item.id}</TableCell>
                                        <TableCell >{item.course_name}</TableCell>
                                        <TableCell >{item.quiz_name}</TableCell>
                                        <TableCell >{
                                            (item.status === 'P' && 'Publish') ||
                                            (item.status === 'D' && 'Draft')

                                        }</TableCell>

                                        <TableCell >

                                            <button onClick={() => editQuiz(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                                <i className="bi bi-pencil-square" title="edit-quiz"></i>
                                            </button>

                                            <button onClick={() => deleteQuiz(item.id)} >
                                                <i className="bi bi-trash" title="delete"></i>
                                            </button>
                                        </TableCell>
                                    </TableRow>
                                ))}


                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[10, 15, 20]}
                        component="div"
                        count={quiz.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>

            </div>
        </IonContent>
    );
}

export default Quiz;
