import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React from 'react';
import { Collapse } from 'react-bootstrap'
import baseUrl, { api } from '../../pages/Urls';
import { format } from "date-fns";
import swal from 'sweetalert';
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DateTimePicker, LocalizationProvider } from "@mui/x-date-pickers";
const AddQuiz: React.FC<{
    collapse: boolean
    onClose: () => void;
    level: any[]
    course_name: string
    fetchShowCourse: () => void

}> = (props) => {
    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
    } = useForm({
        defaultValues: {
            course_name: props.course_name,
            quiz_name: '',
            test_time: '',
            status: '',
            inputlist: [{ level_name: '', qcount: '' }],
            start_date: format(new Date(), "yyyy-MM-dd HH:mm"),

        }
    });

    const { fields, remove, append } = useFieldArray({
        name: 'inputlist',
        control,
    });

    const addQuiz = (e: any) => {
        {

            fetch(`${baseUrl}${api.addQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        props.onClose();
                        reset({
                            course_name: props.course_name,
                            quiz_name: '',
                            test_time: '',
                            status: '',
                            inputlist: [{ level_name: '', qcount: '' }],
                            start_date: format(new Date(), "yyyy-MM-dd HH:mm"),
                        })
                        swal({
                            title: "Success!",
                            text: "Quiz Created successfully",
                            icon: "success",
                        });
                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <>
            <Collapse in={props.collapse}>

                <div className="container mt-5 mb-5 ">

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <IonToolbar>
                                    <IonTitle><h1 className="text-center display-6">Add Quiz</h1></IonTitle>
                                </IonToolbar>
                            </IonCardTitle>
                        </IonCardHeader>
                        <hr />
                        <IonCardContent>
                            <form onSubmit={handleSubmit(addQuiz)}>
                                <IonItem>
                                    <IonLabel position="stacked">Quiz Name</IonLabel>
                                    <IonInput placeholder="quiz name"
                                        type="text"
                                        {...register("quiz_name", { required: "Quiz Name Is Required" })}
                                    >
                                    </IonInput>
                                </IonItem>
                                <p style={{ color: "red" }}>
                                    <ErrorMessage errors={errors} name="quiz_name" />
                                </p>
                                <IonItem>
                                    <IonText color="primary">
                                        <h2 className="mt-3 mb-3">Add Quiz Level</h2>
                                    </IonText>

                                </IonItem>
                                <div>

                                    {
                                        fields.map((x, i) => {

                                            return (
                                                <div key={i.toString()} className="row mb-3 mt-3">
                                                    <div className=" col-md-4">
                                                        <IonItem>
                                                            <IonLabel position="stacked">Level</IonLabel>
                                                            <IonSelect

                                                                {...register(`inputlist.${i}.level_name`, { required: "Level Name Is Required" })}
                                                            >
                                                                {props.level.map((item: any, index: any) => (
                                                                    < IonSelectOption key={index.toString()}>{item.level_name}</IonSelectOption>
                                                                ))}
                                                            </IonSelect>
                                                        </IonItem>
                                                        <p style={{ color: "red" }}>
                                                            <ErrorMessage errors={errors} name={`inputlist.${i}.level_name`} />
                                                        </p>
                                                    </div>
                                                    <div className=" col-md-4">
                                                        <IonItem>
                                                            <IonLabel position="stacked">No of Question</IonLabel>
                                                            <IonInput type="number"
                                                                min={1}
                                                                step='1'
                                                                {...register(`inputlist.${i}.qcount`, { required: "Question No Is Required" })}
                                                            >
                                                            </IonInput>
                                                        </IonItem>
                                                        <p style={{ color: "red" }}>
                                                            <ErrorMessage errors={errors} name={`inputlist.${i}.qcount`} />
                                                        </p>
                                                    </div>
                                                    <div className="col-md-4 mt-4">
                                                        {
                                                            fields.length !== 1 &&
                                                            <button onClick={() => remove(i)}>X</button>
                                                        }
                                                        {
                                                            fields.length - 1 === i &&
                                                            <button className="mx-3 " onClick={() => append({ level_name: '', qcount: '' })}>
                                                                <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                            </button>
                                                        }


                                                    </div>



                                                </div>
                                            );
                                        })}

                                </div>


                                <IonItem>
                                    <IonLabel position="stacked">Status</IonLabel>

                                    <IonSelect
                                        placeholder="status"

                                        {...register("status", { required: "Status Is Required" })}
                                    >

                                        <>
                                            <IonSelectOption value='P'>Publish</IonSelectOption>
                                            <IonSelectOption value='D'>Draft</IonSelectOption>

                                        </>

                                    </IonSelect>

                                </IonItem>
                                <p style={{ color: "red" }}>
                                    <ErrorMessage errors={errors} name="status" />
                                </p>
                                <IonItem>
                                    <IonLabel position="stacked">Duration (Minutes)</IonLabel>

                                    <IonInput type="number"
                                        min={1}
                                        step='1'
                                        {...register("test_time", { required: "Test Time Is Required" })}

                                    >
                                    </IonInput>
                                </IonItem>
                                <p style={{ color: "red" }}>
                                    <ErrorMessage errors={errors} name="test_time" />
                                </p>
                                <IonItem >
                                    <IonLabel position="floating">Quiz Start </IonLabel>
                                    <Controller
                                        name="start_date"
                                        control={control}
                                        rules={{
                                            required: 'Date is required',
                                        }}
                                        defaultValue={format(new Date(), "yyyy-MM-dd HH:mm")}
                                        render={({ field: { ...rest } }) => (
                                            <LocalizationProvider dateAdapter={AdapterDateFns}>
                                                <DateTimePicker

                                                    inputFormat="yyyy-MM-dd HH:mm"

                                                    value={rest.value}
                                                    onChange={(e: any) => {
                                                        setValue("start_date", format(e, "yyyy-MM-dd HH:mm"));

                                                    }}
                                                    renderInput={(params) => (
                                                        <TextField {...params} sx={{ mt: 3, width: '100%' }} />
                                                    )}
                                                />
                                            </LocalizationProvider>
                                        )}
                                    />
                                </IonItem>
                                <p style={{ color: "red" }}>
                                    <ErrorMessage errors={errors} name="start_date" />
                                </p>
                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => {
                                        props.onClose();
                                        //setinputList([]);
                                    }}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary" type="submit">
                                        Create
                                    </IonButton>

                                </div>
                            </form>
                        </IonCardContent>
                    </IonCard>

                </div>


            </Collapse>
        </>
    )
}
export default AddQuiz;
