import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, RemoveRedEye, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { IonContent, IonItem, IonLabel } from '@ionic/react';
import { Link } from 'react-router-dom';
import { Typography } from '@mui/material';
type topicData = {

    id: string;
    topic_name: string;




};

//nested data is ok, see accessorKeys in ColumnDef below


const TchrTopic: FC<{

    topic: any
    course_name: string
    courseId: string
    fetchShowCourse: () => void;
}> = (props) => {

    const [modalOpen, setModalOpen] = useState(false);


    const columns = useMemo<MRT_ColumnDef<topicData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },
            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },


        ],
        [],
    );

    const deleteTopic = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopic}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {

                        props.fetchShowCourse();
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <h1 className='text-center mb-2'>List of Topics</h1>
            <MaterialReactTable
                columns={columns}
                data={props.topic}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="View">
                            <IconButton>
                                <Link className='text-decoration-none' to={`/dashboard/mycourse/${props.courseId}/topic/${row.id}`}>  <RemoveRedEye /></Link>
                            </IconButton>

                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteTopic(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Course">
                        <IconButton onClick={() => setModalOpen(true)}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}
            />
            <CreateTopic
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)

                }}
                fetchShowCourse={() => props.fetchShowCourse()}
                course_name={props.course_name}
            />
        </div>


    );

};

const CreateTopic: FC<{
    onClose: () => void;
    fetchShowCourse: () => void;
    open: boolean;
    course_name: string
}> = (props) => {

    const [topic_name, setTopic_name] = useState('');
    const addCourse = (e: any) => {
        {
            let data = {
                'course_name': props.course_name,
                'topic_name': topic_name,
            }

            fetch(`${baseUrl}${api.addTopic}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Topic Successfully');
                        props.fetchShowCourse();
                        props.onClose();
                        setTopic_name('');
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">Create Topic</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >

                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Toppic Name"
                        variant="filled"
                        focused
                        value={topic_name}
                        onChange={(e: any) => setTopic_name(e.target.value)} />

                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={addCourse}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default TchrTopic;
