import {
    IonContent,
    IonCard, IonCardContent, IonCardHeader, IonCardTitle,
    IonCol, IonGrid, IonRow,
    IonItem, IonButton, IonNote,

} from '@ionic/react';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';
import '../../../pages/Style.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useParams } from "react-router";
import { Link } from 'react-router-dom';
import { format } from 'date-fns';
import ReactPaginate from "react-paginate";
const CategoryBlog: React.FC = () => {
    type CategoryBlogParams = {
        slug: string;
    };
    const [article, setArticle] = useState([]);
    const [search, setSearch] = useState('');
    const { slug } = useParams<CategoryBlogParams>();

    const [pageNumber, setPageNumber] = useState(0);
    const articlePerPage = 10;
    const pagesVisited = pageNumber * articlePerPage;
    const pageCount = Math.ceil(article.length / articlePerPage);
    const changePage = ({ selected }: any) => {
        setPageNumber(selected);
    };

    useEffect(() => {
        fetch(`${baseUrl}${api.catBlog}${slug}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp)
            })

    }, [])
    return (
        <IonContent>

            <div className="container mt-3">
                <h2 >Recent Articles Post</h2>
                <hr />
                <IonSearchbar onIonChange={(e: any) => setSearch(e.target.value)}></IonSearchbar>
                <Paper sx={{ width: '100%', mb: 2, mt: 3 }}>

                    <TableContainer>
                        <Table aria-label="simple table">

                            <TableBody>
                                {(rowsPerPage > 0
                                    ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : article
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.title.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell >
                                            <div>
                                                <h4 className="text-primary"><Link className='text-decoration-none' to={`/dashboard/blog/view/${item.slug}`}>{item.title}</Link></h4>
                                            </div>
                                            <div>
                                                {format(new Date(item.date), 'yyyy-MM-dd')}
                                            </div>

                                        </TableCell>


                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[10, 15, 20]}
                        component="div"
                        count={article.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>
            </div>




        </IonContent>

    );
}


export default CategoryBlog;
