import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../pages/Urls';
const Blog: React.FC = () => {
    const [article, setArticle] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }, [])


    return (
        <>
            <div className="container mt-3">
                <h2>Recent Articles Post</h2>
                <hr />
                <div>

                    {article.map((item: any, i: any) =>
                        <div className="card mb-3">
                            <div className="mt-3 mx-3" >

                                <h4 className="text-primary" >{item.title}</h4></div>


                            <div className="mt-2 mx-3" ><p>{item.date}</p></div>

                        </div>
                    )}


                </div>
            </div>

        </>

    );
}
export default Blog;
