import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonText,
    IonTextarea,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from '../../../pages/Urls';


const Course: React.FC = () => {

    const [course, setCourse] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [course_name, setCourse_name] = useState('');
    const [description, setDescription] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const [id, setId] = useState('');
    const [error, setError] = useState("");
    const [search, setSearch] = useState('');
    useEffect(() => {
        fetch(`${baseUrl}${api.allCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }, []);


    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - course.length) : 0;

    const addCourse = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'course_name': course_name,
                'description': description,
            }
            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Course Successfully');
                        window.location.href = '/dashboard/course';
                        setError("");

                    }
                    else {
                        if (!course_name) {
                            setError("Please fill all input Field");
                        }
                        //throw Error([response.status, response.statusText].join(' '));
                        //(!name || !address || !email || !mobile) 
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    const editCourse = (id: any) => {

        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setCourse_name(result.course.course_name);
                setDescription(result.course.description);
                setId(result.course.id);

            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const updateCourse = () => {
        let data = {
            'course_name': course_name,
            'description': description,
        }
        fetch(`${baseUrl}${api.updateCourse}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Course Successfully');
                    window.location.href = '/dashboard/course';

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteCourse = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteCourse}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        window.location.href = '/dashboard/course';

                    }

                })
                .catch(error => console.log('error', error));
        }
    }


    return (
        <IonContent>
            <div className="container mt-5">

                <>
                    <IonModal isOpen={isOpen}>
                        <IonHeader>
                            <IonToolbar>
                                <IonTitle>Add-Course</IonTitle>
                                <IonButtons slot="end">
                                    <IonButton onClick={() => setIsOpen(false)}>Close</IonButton>
                                </IonButtons>
                            </IonToolbar>
                        </IonHeader>
                        <IonContent className="ion-padding">
                            <IonItem>
                                <IonText color="danger">
                                    <p>{error}</p>
                                </IonText>
                                <IonLabel position="stacked">Course Name</IonLabel>
                                <IonInput
                                    type="text"
                                    placeholder="course name..."
                                    onIonChange={(e: any) => setCourse_name(e.target.value)}
                                    clearInput></IonInput>
                            </IonItem>
                            <IonItem>
                                <IonText color="danger">
                                    <p>{error}</p>
                                </IonText>
                                <IonLabel position="stacked">Course Description</IonLabel>
                                <IonTextarea
                                    placeholder="description..."
                                    onIonChange={(e: any) => setDescription(e.target.value)}
                                ></IonTextarea>
                            </IonItem>
                        </IonContent>
                        <IonContent>
                            <div className="text-center">
                                <IonButton onClick={addCourse}>
                                    Add-Course
                                </IonButton>
                            </div>
                        </IonContent>
                    </IonModal>
                </>

                <div id="demo" className="collapse mt-3 mb-3">

                    <>
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Course</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Course Name</IonLabel>
                                        <IonInput type="text"
                                            name="course_name"
                                            value={course_name}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setCourse_name(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>

                                        <IonLabel position="stacked">Course Description</IonLabel>
                                        <IonTextarea
                                            name="description"
                                            value={description}
                                            placeholder="description..."
                                            onIonChange={(e: any) => setDescription(e.target.value)}
                                        ></IonTextarea>
                                    </IonItem>

                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="primary" onClick={updateCourse}>
                                            Update Course
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </>

                </div>

                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Courses</p>
                    <div className="input-group rounded">
                        <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />

                    </div>
                    <div className="float-end">
                        <button type="button"
                            className="btn btn-light mx-5 my-2"
                            onClick={() => setIsOpen(true)}
                        >
                            <i className="bi bi-plus-circle-fill" title="add-course"></i>
                        </button>

                    </div>
                    {course.length > 0 ?
                        <>
                            <TableContainer>
                                <Table aria-label="simple table">
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Id</TableCell>
                                            <TableCell >Course Name</TableCell>
                                            <TableCell >Description</TableCell>
                                            <TableCell >Action</TableCell>

                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {(rowsPerPage > 0
                                            ? course.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                            : course
                                        ).filter((item: any) => {
                                            return search.toLowerCase() === '' ? item :
                                                item.course_name.toLowerCase().includes(search);

                                        }).map((item: any, i: any) => (
                                            <TableRow key={i.toString()}>
                                                <TableCell component="th" scope="row">{item.id}</TableCell>
                                                <TableCell >{item.course_name}</TableCell>
                                                <TableCell >{item.description}</TableCell>

                                                <TableCell >

                                                    <button onClick={() => editCourse(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                                        <i className="bi bi-pencil-square" title="edit-course"></i>
                                                    </button>


                                                    <button onClick={() => deleteCourse(item.id)} >
                                                        <i className="bi bi-trash" title="delete"></i>
                                                    </button>
                                                </TableCell>
                                            </TableRow>
                                        ))}


                                    </TableBody>
                                </Table>

                            </TableContainer>
                            <TablePagination
                                rowsPerPageOptions={[10, 15, 20]}
                                component="div"
                                count={course.length}
                                rowsPerPage={rowsPerPage}
                                page={page}
                                onPageChange={handleChangePage}
                                onRowsPerPageChange={handleChangeRowsPerPage}
                            />
                        </>
                        :

                        <h6 className="text-center text-warning bg-dark">No data preview</h6>
                    }
                </Paper>

            </div>
        </IonContent>
    );
}

export default Course;
