import {
    IonButtons,
    IonButton,
    IonModal,
    IonHeader,
    IonContent,
    IonToolbar,
    IonTitle,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';
import baseUrl, { api } from '../../pages/Urls';
const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};
const TopicFrom: React.FC<{
    open: boolean
    handleClose: any
    course_name: string
}> = (props) => {
    const [course_name, setCourse_name] = useState('');
    const [topic_name, setTopic_name] = useState('');
    const addTopic = (e: any) => {
        {
            let data = {
                'course_name': props.course_name,
                'topic_name': topic_name,

            }
            fetch(`${baseUrl}${api.addTopic}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Topic created Successfully');
                        window.location.href = '/dashboard/mycourse/4/topic';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <div>

            <Modal
                open={props.open}
                onClose={props.handleClose}
                aria-labelledby="modal-modal-title"

            >
                <Box sx={style}>
                    <div className="text-end">
                        <Button onClick={props.handleClose} variant="text">X</Button>
                    </div>
                    <Typography sx={{ mt: 3 }} id="modal-modal-title" variant="h6" component="h2">
                        Add Topic
                    </Typography>
                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Topic Name"
                        variant="filled"
                        focused
                        onChange={(e: any) => setTopic_name(e.target.value)} />
                    <TextField sx={{ mt: 3 }}
                        type="file"
                        label="Topic File"
                        variant="filled"
                        focused />
                    <Button sx={{ mt: 3 }} variant="outlined" onClick={addTopic}>Add</Button>
                </Box>
            </Modal>
        </div>
    );
}
export default TopicFrom;
