import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonText,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import baseUrl, { api } from '../../pages/Urls';
import swal from 'sweetalert';
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
const AddQuestion: React.FC<{

    level: any[]
    collapse: boolean
    onClose: () => void;
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {
    const [qType, setQType] = useState('');
    const [topic, setTopic] = useState([]);

    const {
        handleSubmit,
        register,
        unregister,
        formState: { errors },
        reset,
        control,
        setValue,
        watch,
    } = useForm({
        defaultValues: {

            level_id: '',
            topic_name: '',
            title: '',
            q_content: '',
            question_type: '',
            answer: '',
            option: '',


        }

    });



    ;

    const handleSelectChange = (e: any) => {

        const lid = e.target.value;

        if (lid) {
            fetch(`${baseUrl}${api.allLevelTopic}${lid}`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }
            })
                .then(resp => resp.json())
                .then(resp => {
                    setTopic(resp)
                })
        }

    }

    const addQuestion = (e: any) => {
        {

            fetch(`${baseUrl}${api.addQuestion}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        props.onClose();

                        reset({
                            level_id: '',
                            topic_name: '',
                            title: '',
                            q_content: '',
                            question_type: '',
                            answer: '',
                            option: ''
                        })

                        swal({
                            title: "Success!",
                            text: "Question Created successfully",
                            icon: "success",
                        });

                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }

    return (
        <Collapse in={props.collapse}>
            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">add Question</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>
                        <form onSubmit={handleSubmit(addQuestion)}>
                            <IonItem>
                                <IonLabel position="stacked">Level</IonLabel>
                                <IonSelect

                                    onIonChange={(e: any) => handleSelectChange(e)}
                                    {...register("level_id", { required: "Level Name Is Required" })}
                                    placeholder="Select level" >
                                    {props.level.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()} value={item.id}>{item.level_name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name='level_id' />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked">Topic</IonLabel>
                                <IonSelect
                                    {...register("topic_name", { required: "Topic Name Is Required" })}

                                    placeholder="Select topic" >
                                    {topic.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name='topic_name' />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked">Question Type</IonLabel>

                                <IonSelect
                                    onIonChange={(e: any) => setQType(e.target.value)}
                                    placeholder="Select question_type"
                                    {...register("question_type", { required: "Question type Is Required" })}
                                >

                                    <>
                                        <IonSelectOption value='MCQ'>MCQ</IonSelectOption>
                                        <IonSelectOption value='SQ'>Short Question</IonSelectOption>

                                    </>

                                </IonSelect>

                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name='question_type' />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked">Question</IonLabel>
                                <IonInput type="text"

                                    {...register("title", { required: "Question Title Is Required" })}></IonInput>
                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name='title' />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked" >Content</IonLabel>
                                <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                    <Controller
                                        name="q_content"
                                        control={control}

                                        render={({ field: { ...rest } }) => (
                                            <Editor
                                                textareaName="q_content"
                                                value={rest.value}

                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'

                                                }}
                                                onEditorChange={(e) => {
                                                    setValue('q_content', e)
                                                }}
                                            />

                                        )}
                                    />
                                </div>

                            </IonItem>
                            {qType === 'MCQ' ?
                                <>
                                    <IonItem>
                                        <IonText color="primary">
                                            <h2 className="mt-3 mb-3">Add Question Option</h2>
                                        </IonText>

                                    </IonItem>
                                    <div>

                                    </div>
                                    <IonItem>
                                        <IonLabel position="stacked">Option</IonLabel>
                                        <IonInput type="text"

