import {
    IonContent,
    IonItem,
    IonLabel,
    IonSegment,
    IonSegmentButton,
    IonRouterOutlet,
    IonCard,
    IonAccordion,
    IonAccordionGroup,
    IonText,
    IonCardContent,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation, Link } from "react-router-dom";
import baseUrl, { api } from '../../../pages/Urls';



const UserCourse: React.FC = () => {
    type UserCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<UserCourse>();
    const [course_name, setCourse_name] = useState('');
    const [courseId, setCourseId] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [level, setLevel] = useState([]);
    const [question, setQuestion] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const pathname = history.location.pathname;
    useEffect(() => {
        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setCourseId(result.course.id)
                setQuiz(result.course.quiz)
                setTopic(result.course.topic)
                setLevel(result.course.level)
                setQuestion(result.course.question)
            })

    }, []);

    return (
        <>
            <IonContent fullscreen={true}>

                <IonText color="primary">
                    <h1 className='mx-2'>{course_name}</h1>
                </IonText>

                <List quiz={quiz} topic={topic} />

            </ IonContent>

        </>
    );


}

const List: React.FC<{
    quiz: any[],
    topic: any[]

}> = (props) => {

    return (

        <>

            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Quiz</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            {props.quiz.map((item: any, i: any) =>
                                <h3>
                                    <Link className='text-decoration-none' to={`/dashboard/mycourse/${item.course_id}`}> {item.quiz_name}</Link>
                                </h3>
                            )}
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>

            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Topic</IonLabel>
                        </IonItem>
                        <IonItem slot="header" color="light">

                            <IonLabel>Totaal Topic : {props.topic.length}</IonLabel>
                        </IonItem>
                        <div className="ion-padding" slot="content">
                            {props.topic.map((item: any, i: any) =>
                                <p key={i.toString()}>{i + 1}. {item.topic_name}</p>
                            )}
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>

        </>


    );
};

export default UserCourse;
