import {
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonContent,
    IonMenu,
    IonList,
    IonItem,
    IonRouterOutlet,
    IonMenuButton,
    IonButtons
} from '@ionic/react';
import React, { Component } from "react";
import { Redirect } from 'react-router-dom';
import AddArticle from '../components/articles/AddArticle';
import Articles from './../components/articles/Articles';
import Group from './Group';


class Dashboard extends Component<any, any>{
    constructor(props: any) {
        super(props);

        this.state = {

            redirectToReferrer: false,
            token: null,

        };
        this.logout = this.logout.bind(this);
        this.manageArticle = this.manageArticle.bind(this);
        this.manageGroup = this.manageGroup.bind(this);
    }


    componentWillMount() {

        if (localStorage.getItem('token')) {
            this.setState({
                token: localStorage.getItem("token")
            });

        }

        else {
            this.setState({ redirectToReferrer: true });
        }


    }

    logout() {
        localStorage.setItem('token', '');
        localStorage.clear();
        this.setState({ redirectToReferrer: true });
    }
    manageArticle() {
        this.setState({ redirectToReferrer: false });
    }
    manageGroup() {
        this.setState({ redirectToReferrer: false });
    }

    render() {
        const { token } = this.state;
        if (this.state.redirectToReferrer) {
            return (<Redirect to={'/login'} />)
        }
        if (this.state.redirectToReferrer) {
            return (<Redirect to={'/group'} />)
        }
        return (
            <IonPage>
                <IonHeader>
                    <IonToolbar>
                        <IonButtons slot="start">
                            <IonMenuButton menu="first"></IonMenuButton>
                        </IonButtons>

                        <IonTitle>Dashboard</IonTitle>

                    </IonToolbar>
                </IonHeader>
                <IonMenu side="start" menuId="first" contentId="main">
                    <IonHeader>
                        <IonToolbar color="medium">
                            <IonTitle>Dashboard</IonTitle>
                        </IonToolbar>
                    </IonHeader>
                    <IonContent>
                        <IonList>
                            <IonItem onClick={(e: any) => this.manageArticle()}>Manage Article</IonItem>
                            <IonItem onClick={(e: any) => this.manageGroup()}>Manage Group</IonItem>
                            <IonItem>Menu Item</IonItem>
                            <IonItem onClick={(e: any) => this.logout()}>logout</IonItem>
                        </IonList>
                    </IonContent>
                </IonMenu>
                <IonRouterOutlet id="main"></IonRouterOutlet>

                <IonContent>

                    {/* <AddArticle />
                    <Articles token={token} /> */}
                    <Group />
                </IonContent>

            </IonPage>
        );
    }
}

export default Dashboard;
