import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,

} from "@ionic/react";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import TablePagination from '@mui/material/TablePagination';
import { Editor } from '@tinymce/tinymce-react';
import React from "react";
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from "../../pages/Urls";


class Articles extends React.Component<any, any>{
    constructor(props: any) {
        super(props);
        this.state = {
            articles: [],
            article: [],
            token: props.token,
            redirectToReferrer: false,
            title: "",
            slug: "",
            content: "",
            status: "",
            date: "",
            id: '',
            page: 0,
            rowsPerPage: 10,
        };
        this.deleteArticle = this.deleteArticle.bind(this);
        this.editArticle = this.editArticle.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleEditorChange = this.handleEditorChange.bind(this);
        this.updateArticle = this.updateArticle.bind(this);
        this.handleChangePage = this.handleChangePage.bind(this);
        this.handleChangeRowsPerPage = this.handleChangeRowsPerPage.bind(this);

    }

    componentDidUpdate(prevProps: any) {

        if (this.props != prevProps) {
            this.setState({
                token: this.props.token,

            });
        }
    }
    componentDidMount() {
        const { token } = this.state;
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`

            }
        })
            .then(res => res.json())
            .then(
                (data) => {
                    this.setState({ articles: data.reverse() });
                },
                (error) => {
                    alert(error);
                }
            )
    }

    editArticle(id: any) {
        // const { articles, show, title, content, date } = this.state;
        fetch(`${baseUrl}${api.editArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                this.setState({
                    title: result.article.title,
                    slug: result.article.slug,
                    content: result.article.content,
                    status: result.article.status,
                    date: result.article.date,
                    id: result.article.id,
                });
            })
            .catch(function (error) {
                console.log(error);
            })


    }


    deleteArticle(id: any) {


        const { token } = this.state;
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        this.setState({ redirectToReferrer: true });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }

    handleChange(e: React.ChangeEvent<any>) {
        this.setState({
            [e.target.name]: e.target.value,


        })
    }

    handleEditorChange(content: any, editor: any) {
        this.setState({ content });
    }


    updateArticle() {
        const { id } = this.state;

        fetch(`${baseUrl}${api.updatetArticle}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify({
                title: this.state.title,
                slug: this.state.slug,
                content: this.state.content,
                status: this.state.status,
                date: this.state.date
            }),
        })

            .then(res => res.json())
            .then(result => {
                if (result) {
                    this.setState({ redirectToReferrer: true });
                }

            })
            .catch(error => console.log('error', error));


    }
    handleChangePage = (e: any, newPage: any) => {
        this.setState(this.state.page(newPage));
    };

    handleChangeRowsPerPage = (e: any) => {
        this.setState(this.state.rowsPerPage(parseInt(e.target.value, 10)));
        this.setState(this.state.page(0));
    };

    render() {
        if (this.state.redirectToReferrer) {
            return (<Redirect to={'/dashboard'} />)
        }
        const { articles, page, rowsPerPage } = this.state;

        return (
            <>
                <div className="container mt-3">
                    <div id="demo" className="collapse mt-3 mb-3">

                        <>
                            <div className="container mt-5 mb-5 ">


                                <IonCard>
                                    <IonCardHeader>
                                        <IonCardTitle>
                                            <IonToolbar>
                                                <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                            </IonToolbar>
                                        </IonCardTitle>
                                    </IonCardHeader>
                                    <hr />
                                    <IonCardContent>
                                        <IonItem>
                                            <IonLabel position="stacked">Title</IonLabel>
                                            <IonInput type="text"
                                                name="title"
                                                value={this.state.title}
                                                required
                                                clearInput
                                                onIonChange={(e: any) => this.handleChange(e)}
                                            >
                                            </IonInput>
                                        </IonItem>
                                        <IonItem>
                                            <IonLabel position="stacked">Slug</IonLabel>
                                            <IonInput type="text"
                                                name="slug"
                                                value={this.state.slug}
                                                required
                                                clearInput
                                                onIonChange={(e: any) => this.handleChange(e)}
                                            >
                                            </IonInput>
                                        </IonItem>
                                        <IonItem>
                                            <IonLabel position="stacked" >Content</IonLabel>
                                            <div style={{ marginTop: "3%" }}>
                                                <Editor
                                                    textareaName="content"
                                                    value={this.state.content}
                                                    onEditorChange={this.handleEditorChange}
                                                    init={{
                                                        height: 500,
                                                        width: 1030,
                                                        menubar: true,
                                                        plugins: [

                                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                        ],

                                                        toolbar: 'undo redo | blocks | ' +
                                                            'bold italic forecolor | alignleft aligncenter ' +
                                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                                            'removeformat | help',
                                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                    }}
                                                />
                                            </div>

                                        </IonItem>
                                        <IonItem>
                                            <IonLabel position="stacked">Status</IonLabel>
                                            <IonInput type="text"
                                                name="status"
                                                value={this.state.status}
                                                required
                                                clearInput
                                                onIonChange={(e: any) => this.handleChange(e)}
                                            >
                                            </IonInput>
                                        </IonItem>
                                        <IonItem>
                                            <IonLabel position="stacked">Date</IonLabel>
                                            <IonInput type="datetime-local"
                                                name="date"
                                                value={this.state.date}
                                                required
                                                clearInput
                                                onIonChange={(e: any) => this.handleChange(e)}
                                            >
                                            </IonInput>
                                        </IonItem>
                                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                            <IonButton color="primary" onClick={(this.updateArticle)}>
                                                Update Article
                                            </IonButton>

                                        </div>
                                    </IonCardContent>
                                </IonCard>

                            </div>

                        </>

                    </div>

                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 750 }} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Sl</TableCell>
                                    <TableCell >Created By</TableCell>
                                    <TableCell >Title</TableCell>
                                    <TableCell >Slug</TableCell>
                                    <TableCell >Status</TableCell>
                                    <TableCell >Date</TableCell>
                                    <TableCell >Action</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {articles.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((item: any, i: any) => (
                                    <TableRow
                                        key={i.toString()}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell component="th" scope="row">{item.id}</TableCell>
                                        <TableCell >{item.user.name}</TableCell>
                                        <TableCell >{item.title}</TableCell>
                                        <TableCell >{item.slug}</TableCell>
                                        <TableCell>
                                            <Typography style={{ display: (((item.status) == 'P') ? 'Publish' : 'Draft') }}>
                                                {item.status}</Typography></TableCell>
                                        <TableCell >{item.date}</TableCell>
                                        <TableCell >
                                            <button className="mx-3" onClick={() => this.editArticle(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                                <i className="bi bi-pencil-square"></i>
                                            </button>

                                            <button onClick={() => this.deleteArticle(item.id)} >
                                                <i className="bi bi-trash"></i>
                                            </button>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                        <hr />
                        <TablePagination
                            rowsPerPageOptions={[10, 20, 30]}
                            component="div"
                            count={articles.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={this.handleChangePage}
                            onRowsPerPageChange={this.handleChangeRowsPerPage}
                        />
                    </TableContainer>

                </div>

            </>

        );
    }


}



export default Articles;
