import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,

} from '@ionic/react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import './Blog.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useNavigate } from "react-router";
const Blog: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(2);
    const navigate = useNavigate();
    //const history = useHistory();
    // const [searchPhrase, setSearchPhrase] = useState("");
    // const search = (event: any) => {
    //     const matchedArticle = article.filter((arr) => {
    //         return `${arr.title}`
    //             .toLowerCase()
    //             .includes(event.target.value.toLowerCase());
    //     });

    //     setArticle(matchedArticle);
    //     setSearchPhrase(event.target.value);
    // };

    useEffect(() => {
        fetch(`${baseUrl}${api.authBlog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }, [])
    const showArticle = (id: any) => {
        history.push("/dashboard/view/blog", { state: { title: title } });

        fetch(`${baseUrl}${api.viewArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
            })
            .catch(function (error) {
                console.log(error);
            })

    };

    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - article.length) : 0;


    return (
        <>
            <div className="container mt-3">
                <h2>Recent Articles Post</h2>
                <hr />
                {/* <div className="search-container">
                    <input
                        type="text"
                        placeholder="Search"
                        value={searchPhrase}
                        onChange={search}
                    />
                </div> */}
                <Paper sx={{ width: '100%', mb: 2, mt: 3 }}>

                    <TableContainer>
                        <Table aria-label="simple table">

                            <TableBody>
                                {(rowsPerPage > 0
                                    ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : article
                                ).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell >
                                            <div>
                                                <h4 className="text-primary" onClick={(e) => showArticle(item.id)}>{item.title}</h4>
                                            </div>
                                            <div>
                                                {item.date}
                                            </div>

                                        </TableCell>


                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[2, 3, 7]}
                        component="div"
                        count={article.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>
            </div>
        </>

    );
}


export default Blog;
