import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
const QuizResult: React.FC = () => {
    type QuizResultParams = {
        id: string;
    };
    const history = useHistory();
    const [point, setPoint] = useState([]);
    const [correct_ans, setCorrectAns] = useState('');
    const [total_question, setTotalQuestion] = useState('');
    const { id } = useParams<QuizResultParams>();
    useEffect(() => {

        fetch(`${baseUrl}${api.quizResult}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setPoint(result.map((op: any) => (op.points)))
                setTotalQuestion(result.length);

            })
            .catch(function (error) {
                console.log(error);
            })


    }, [])

    const count = point.reduce((accumulator, p) => {
        if (p > 0) {
            return accumulator + 1;
        }
        return false;
    }).length;
    //console.log('dd', count);
    return (
        <>

            <IonCard>
                <IonCardHeader>
                    <div className='text-center mb-3'>
                        <IonCardTitle>
                            <IonText color="primary">
                                <h1>Congratulations!</h1>

                            </IonText>

                        </IonCardTitle>
                        <IonCardSubtitle>You have finished online Quiz Successfully  </IonCardSubtitle>
                    </div>

                </IonCardHeader>

                <IonCardContent>
                    <h1>Correct answer: {count}/{total_question}</h1>
                    <h1>Total Mark: 2/4</h1>

                </IonCardContent>
                <div className='text-center mt-3 mb-5'>
                    <IonButton fill="outline" href="/dashboard">Dashboard</IonButton>
                </div>
            </IonCard>

        </>

    );
}
export default QuizResult;
