import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonNote,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Collapse } from 'react-bootstrap'
import { useForm, Controller } from "react-hook-form";
const AddCourse: React.FC<{

    collapse: boolean
    instructor: any[]
    onClose: () => void;
    fetchCourse: () => void;

}> = (props) => {

    const [course_name, setCourse_name] = useState('');
    const [description, setDescription] = useState('');
    const [crs_inst, setCrs_inst] = useState([]);
    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState: { errors }
    } = useForm({
        defaultValues: {
            course_name: '',
            description: '',
            crs_inst: [],

        }
    });


    const addCourse = (e: any) => {
        {
            e.preventDefault();

            let data = {
                'course_name': course_name,
                'description': description,
                'crs_inst': crs_inst,
            }

            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {

                        props.onClose();
                        props.fetchCourse();
                        setCourse_name('');
                        setDescription('')
                        setCrs_inst([])
                    }

                    else {
                        throw new Error("Status code: " + response.status);
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    // console.log(error.course_name)

    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">
                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Create Course</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>

                        <IonItem >
                            <IonLabel position="stacked">Course Name</IonLabel>
                            <IonInput
                                {...register('course_name', {
                                    required: 'course_name is a required field',

                                })}

                            >
                            </IonInput>
                            <IonNote slot="error"></IonNote>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Description</IonLabel>
                            <IonInput
                                {...register('description')}


                            >
                            </IonInput>
                        </IonItem>

                        <IonItem >
                            <IonLabel position="stacked">Course Instructor</IonLabel>
                            <IonSelect
                                name="crs_inst"
                                value={crs_inst}
                                onIonChange={(e: any) => setCrs_inst(e.target.value)}

                                placeholder="Select instructors" multiple={true}>
                                {props.instructor.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                ))}
                            </IonSelect>
                            <IonNote slot="error"></IonNote>
                        </IonItem>


                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={props.onClose}>
                                Cancel
                            </IonButton>
                            <IonButton color="primary" onClick={addCourse}>
                                Save
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>


        </Collapse>
    );
}

export default AddCourse;
