import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import baseUrl, { api } from '../../pages/Urls';
const AddQuestion: React.FC<{

    level: any[]
    collapse: boolean
    onClose: () => void;
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {


    const [level_name, setLevel_name] = useState('');
    const [topic_name, setTopic_name] = useState('');
    const [question, setQuestion] = useState('');
    const [q_content, setQContent] = useState('');
    const [questionType, setQuestionType] = useState('');
    const [answer, setAnswer] = useState('');
    const [option, setOption]: any[] = useState([]);
    const [lvlid, setLvlid] = useState('');
    const [topic, setTopic] = useState([]);
    const handleAdd = () => {
        const abc = [...option, []]
        setOption(abc)
    }
    const handleChange = (onChangeValue: any, i: any) => {
        const inputdata = [...option]
        inputdata[i] = onChangeValue.target.value;
        setOption(inputdata)
    }
    const handleDelete = (i: any) => {
        const deletVal = [...option]
        deletVal.splice(i, 1)
        setOption(deletVal)
    }
    const handleSelectChange = (e: any) => {
        //setLevel_name(e.target.value);
        //setSelectedState(e.target.value)
        const lid = e.target.value;
        setLevel_name(e.target.name);
        setLvlid(lid);
        fetch(`${baseUrl}${api.allLevelTopic}${lid}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setTopic(resp)
            })
    }

    console.log(topic);
    const addQuestion = (e: any) => {
        {
            let data = {

                'level_name': level_name,
                'title': question,
                'q_content': q_content,
                'question_type': questionType,
                'option': option,
                'answer': answer,

            }
            fetch(`${baseUrl}${api.addQuestion}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create question Successfully');
                        props.fetchShowCourse();
                        props.onClose();
                        setLevel_name('');
                        setQuestionType('');
                        setQuestion('');
                        setQContent('');
                        setAnswer('');
                        setOption([]);

                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <Collapse in={props.collapse}>
            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">add Question</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>
                        <IonItem>
                            <IonLabel position="stacked">Level</IonLabel>
                            <IonSelect
                                name="level_name"
                                // value={level_name}
                                onIonChange={(e: any) => handleSelectChange(e)}

                                placeholder="Select level" >
                                {props.level.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()} value={item.id}>{item.level_name}</IonSelectOption>
                                ))}
                            </IonSelect>

                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Topic</IonLabel>
                            <IonSelect
                                name="topic_name"
                                value={topic_name}
                                onIonChange={(e: any) => handleSelectChange(e)}

                                placeholder="Select topic" >
                                {props.level.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.level_name}</IonSelectOption>
                                ))}
                            </IonSelect>

                        </IonItem>

                        <IonItem>
                            <IonLabel position="stacked">Question Type</IonLabel>

                            <IonSelect
                                name="question_type"
                                placeholder="Select question_type"
                                value={questionType}
                                onIonChange={(e: any) => setQuestionType(e.target.value)}
                            >

                                <>
                                    <IonSelectOption value='MCQ'>MCQ</IonSelectOption>
                                    <IonSelectOption value='SQ'>Short Question</IonSelectOption>

                                </>

                            </IonSelect>

                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Question</IonLabel>
                            <IonInput type="text"
                                name="title"
                                placeholder="question..."
                                onIonChange={(e: any) => setQuestion(e.target.value)}
                                clearInput></IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked" >Content</IonLabel>
                            <div style={{ marginTop: "3%", marginBottom: "5%" }}>
                                <Editor
                                    textareaName="content"
                                    value={q_content}
                                    onEditorChange={(content: any, editor: any) => setQContent(content)}
                                    init={{
                                        height: 500,
                                        width: 1030,
                                        menubar: true,
                                        plugins: [

                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                        ],

                                        toolbar: 'undo redo | blocks | ' +
                                            'bold italic forecolor | alignleft aligncenter ' +
                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                            'removeformat | help',
                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                    }}
                                />
                            </div>

                        </IonItem>
                        {questionType === 'MCQ' ?
                            <>
                                <IonText color="primary">
                                    <h2 className="mt-3 text-center">Add Question Option</h2>
                                </IonText>


                                {option.map((op: any, i: any) => {
                                    return (
                                        <div key={i.toString()} className="mt-2">

                                            <input className="mx-2"
                                                type="radio"
                                                name="answer"
                                                value={op}
                                                onChange={(e: any) => setAnswer(e.target.value)}
                                            />


                                            <input type="text"
                                                name="option[]"
                                                placeholder="options..."
                                                value={op} onChange={e => handleChange(e, i)} />
                                            {
                                                option.length !== 1 &&
                                                <button onClick={() => handleDelete(i)}>x</button>
                                            }
                                            {
                                                option.length - 1 === i &&
                                                <button onClick={() => handleAdd()}>
                                                    <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                </button>
                                            }

                                        </div>
                                    );
                                })}


                            </>
                            :
                            <IonItem>
                                <IonLabel position="stacked">Answer</IonLabel>
                                <IonInput type="text"
                                    name="answer"
                                    placeholder="ans..."
                                    onIonChange={(e: any) => setAnswer(e.target.value)}
                                    clearInput></IonInput>
                            </IonItem>
                        }
                        <hr />
                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={() => {
                                props.onClose();

                            }}>
                                Cancel
                            </IonButton>
                            <IonButton color="primary" onClick={addQuestion}>
                                Create
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>

        </Collapse>
    )
}
export default AddQuestion;
