import {
    IonContent,
    IonCard, IonCardContent, IonCardHeader, IonCardTitle,
    IonCol, IonGrid, IonRow,
    IonItem, IonButton, IonNote,

} from '@ionic/react';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';
import '../../../pages/Style.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory } from "react-router";
import { Link } from 'react-router-dom';
import { format } from 'date-fns'
import ReactPaginate from "react-paginate";
const Blog: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [allCategory, setAllCategory] = useState([]);
    const history = useHistory();
    const [pageNumber, setPageNumber] = useState(0);
    const articlePerPage = 10;
    const pagesVisited = pageNumber * articlePerPage;
    const pageCount = Math.ceil(article.length / articlePerPage);
    const changePage = ({ selected }: any) => {
        setPageNumber(selected);
    };
    const fetchAuthBlog = () => {
        fetch(`${baseUrl}${api.authBlog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }
    const fetchCategory = () => {
        fetch(`${baseUrl}${api.allCategory}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setAllCategory(resp)
            })
    }


    useEffect(() => {
        fetchAuthBlog();
        fetchCategory();
    }, [])

    const search = (text: any) => {
        if (text) {
            let matches = article.filter((item: any) => {
                const regex = new RegExp(`${text}`, "gi");
                return item.title.match(regex);
            })
            setArticle(matches);
        } else {
            fetchAuthBlog();
        }

    };

    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>
                        <div className="container mt-3 mb-5">
                            <h2 >Recent Articles Post</h2>
                            <hr />
                            <Box sx={{ display: 'flex', justifyContent: "flex-end", mx: 1 }}>
                                <TextField
                                    id="input-with-icon-textfield"
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <SearchIcon />
                                            </InputAdornment>
                                        ),
                                    }}
                                    variant="standard"
                                    onChange={(e: any) => search(e.target.value)}
                                />
                            </Box>
                            {
                                article.length != 0 ?
                                    <Box sx={{ width: '100%', mb: 2, mt: 3 }}>


                                        {(articlePerPage > 0
                                            ? article.slice(pagesVisited, pagesVisited + articlePerPage)
                                            : article).map((item: any, i: any) => (
                                                <IonCard key={i.toString()}>
                                                    <IonCardHeader>
                                                        <IonCardTitle>
                                                            <h4 className="text-primary"><Link className='text-decoration-none' to={`/dashboard/blog/view/${item.slug}`}>{item.title}</Link></h4>
                                                        </IonCardTitle>

                                                    </IonCardHeader>

                                                    <IonCardContent>
                                                        {format(new Date(item.date), 'yyyy-MM-dd')}
                                                    </IonCardContent>
                                                </IonCard>

                                            ))}

                                    </Box>
                                    :
                                    <div className='text-center mt-5'>
                                        <IonNote color="danger">**Not match search data**</IonNote>
                                    </div>

                            }


                        </div>
                        {article.length != 0 && article.length > articlePerPage ?
                            <ReactPaginate
                                nextLabel=" >"
                                onPageChange={changePage}
                                pageRangeDisplayed={3}
                                marginPagesDisplayed={2}
                                pageCount={pageCount}
                                previousLabel="< "
                                pageClassName="page-item"
                                pageLinkClassName="page-link"
                                previousClassName="page-item"
                                previousLinkClassName="page-link"
                                nextClassName="page-item"
                                nextLinkClassName="page-link"
                                breakLabel="..."
                                breakClassName="page-item"
                                breakLinkClassName="page-link"
                                containerClassName="pagination"
                                activeClassName="active"
                                renderOnZeroPageCount={null || undefined}
                            /> : ''}
                    </IonCol>

                    <IonCol size="auto">

                        <div style={{ width: "200px" }}>
                            <h3>Categories</h3>

                            <IonItem >
                                <IonButton fill="clear" onClick={() => fetchAuthBlog()}>All Articles</IonButton>
                            </IonItem>
                            {allCategory.map((cat: any, i: any) =>
                                <IonItem key={i.toString()}>
                                    <IonButton fill="clear">
                                        <Link className='text-decoration-none' to={`/dashboard/blog/category/${cat.slug}`}>
                                            {cat.name}
                                        </Link>
                                    </IonButton>
                                </IonItem>
                            )}


                        </div>

                    </IonCol>

                </IonRow>
            </IonGrid>

        </IonContent>

    );
}


export default Blog;
