import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonPopover,
    IonDatetime,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import AddArticle from "./AddArticle";
import { format } from "date-fns";
import swal from 'sweetalert';
import Select from 'react-select';
import { useForm, Controller } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
type article_Data = {
    id: string;
    user: {
        name: string;
    };
    title: string;
    slug: string;
    date: string;
    status: string;


};
const schema = yup.object().shape({
    slug: yup.string()
        .required('Slug is required'),

    title: yup.string()
        .required('Tilte is required'),
    content: yup.string()
        .required('Content is required'),
    category: yup.array().required().min(1),
    status: yup.string()
        .required('Status is required'),
    date: yup.string()
        .required('Date is required'),
})
const Articles: FC = () => {

    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
        clearErrors,
        getValues
    } = useForm({
        defaultValues: {
            id: '',
            title: '',
            slug: '',
            content: '',
            status: '',
            category: [{}],
            gname: [{}],
            date: format(new Date(), "yyyy-MM-dd"),

        },
        resolver: yupResolver(schema),
    });
    const [article, setArticle] = useState([]);
    const [group, setGroup] = useState([]);
    const [category, setCategory] = useState([]);
    const [title, setTitle] = useState('');
    const [slug, setSlug] = useState('');
    const [content, setContent] = useState('');
    const [status, setStatus] = useState('');
    const [id, setId] = useState('');
    const [gname, setGname] = useState([]);
    const [catname, setCatname] = useState([]);
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [articleData, setArticleData] = useState({});
    const [showArtGroup, setShowArtGroup] = useState([]);
    const [showArtCategory, setShowArtCategory] = useState([]);
    const [groupOptions, setGroupOptions] = useState([{}]);
    const [categoryOptions, setCategoryOptions] = useState([{}]);
    const [dateValue, setDateValue] = useState<any>(
        format(new Date(), "yyyy-MM-dd")
    );
    const fetchArticle = () => {
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }
    const fetchGroup = () => {
        fetch(`${baseUrl}${api.allGroup}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setGroup(resp)
                setGroupOptions(resp.map((item: any) => ({
                    label: item.name,
                    value: item.name
                })))
            })
    }
    const fetchCategory = () => {
        fetch(`${baseUrl}${api.allCategory}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCategory(resp)
                setCategoryOptions(resp.map((item: any) => ({
                    label: item.name,
                    value: item.name
                })))
            })
    }
    useEffect(() => {
        fetchArticle();
        fetchGroup();
        fetchCategory();
    }, [])

    const columns = useMemo<MRT_ColumnDef<article_Data>[]>(
        () => [


            {
                accessorKey: 'user.name',
                header: 'Created By',

            },
            {
                accessorKey: 'title',
                header: 'Title',

            },
            {
                accessorKey: 'slug',
                header: 'Slug',

            },
            {
                accessorKey: 'status',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<string>() === "P" && 'Publish' ||
                    cell.getValue<string>() === "D" && 'Draft' ||
                    cell.getValue<string>() === "R" && 'Review'

                ),

            },
            {
                accessorKey: 'date',
                header: 'Date',

            },


        ],
        [],
    );


    const editArticle = (id: any) => {

        fetch(`${baseUrl}${api.editArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setValue('title', result.article.title);
                //setSlug(result.article.slug);
                setValue('content', result.article.content);
                setValue('status', result.article.status);
                setValue('date', result.article.date);
                setValue('id', result.article.id);
                setShowArtGroup(result.article.group);
                setShowArtCategory(result.article.category);
                setValue('gname',

                    result.article.group.map((item: any) => ({
                        label: item.name,
                        value: item.name
                    }))


                );
                setValue('category',

                    result.article.category.map((item: any) => ({
                        label: item.name,
                        value: item.name
                    }))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const updateArticle = () => {
        let data = {
            'title': title,
            'content': content,
            'status': status,
            'date': format(new Date(dateValue), "yyyy-MM-dd"),
            'gname': gname,
            'category': catname,
        }
        fetch(`${baseUrl}${api.updatetArticle}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    fetchArticle();
                    setOpen(false)
                    swal({
                        title: "Success!",
                        text: "Article Updated successfully",
                        icon: "success",
                    });

                } else {
                    swal({
                        title: "Error!",
                        text: res.status + ' ' + res.statusText + " - Article not updated",
                        icon: "error",
                    });
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteArticle = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => {
                    if (res.status === 200) {
                        fetchArticle();

                        swal({
                            title: "Success!",
                            text: "Article move to trash successfully",
                            icon: "success",
                        });
                    } else {
                        swal({
                            title: "Error!",
                            text: res.status + ' ' + res.statusText +
                                " - Article not move to trash",
                            icon: "error",
                        });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }

    return (
        <IonContent>
            <div className="container mt-5">

                <Collapse in={open}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Title</IonLabel>
                                        <IonInput type="text"
                                            name="title"
                                            value={title}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => {
                                                setTitle(e.target.value)

                                            }}
                                        >
                                        </IonInput>
                                    </IonItem>

                                    <IonItem>
                                        <IonLabel position="stacked" >Content</IonLabel>
                                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                            <Editor
                                                textareaName="content"
                                                value={content}
                                                onEditorChange={(content: any, editor: any) => setContent(content)}
                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                }}
                                            />
                                        </div>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Category</IonLabel>
                                        <IonSelect
                                            name="category"
                                            value={catname}
                                            onIonChange={(e: any) => setCatname(e.target.value)}

                                            placeholder="Select category" multiple={true}>
                                            {category.map((item: any, i: any) => (
                                                < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                            ))}
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Group</IonLabel>
                                        <IonSelect
                                            name="gname"
                                            value={gname}
                                            onIonChange={(e: any) => setGname(e.target.value)}

                                            placeholder="Select groups" multiple={true}>
                                            {group.map((item: any, i: any) => (
                                                < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                            ))}
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Status</IonLabel>

                                        <IonSelect
                                            name="status"
                                            value={status}
                                            onIonChange={(e: any) => setStatus(e.target.value)}
                                        >
                                            {(localStorage.getItem('role') == 'ar_staff1') ?
                                                <IonSelectOption value='R'>Review</IonSelectOption> :
                                                <>
                                                    <IonSelectOption value='P'>Publish</IonSelectOption>
                                                    <IonSelectOption value='D'>Draft</IonSelectOption>

                                                </>



                                            }
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem id="open-date-trigger">
                                        <IonLabel position="floating">Date </IonLabel>
                                        <IonInput
                                            type="text"
                                            clearInput
                                            value={format(new Date(dateValue), "yyyy-MM-dd")}
                                        ></IonInput>
                                    </IonItem>
                                    <IonPopover trigger="open-date-trigger" triggerAction="click">
                                        <IonContent>
                                            <IonDatetime onIonChange={(e) => setDateValue(e.target.value)}></IonDatetime>
                                        </IonContent>
                                    </IonPopover>

                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="danger" onClick={() => setOpen(false)}>
                                            Cancel
                                        </IonButton>
                                        <IonButton color="primary" onClick={updateArticle}>
                                            Save
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <AddArticle collapse={collapse} groupOptions={groupOptions} categoryOptions={categoryOptions} onClose={() => setCollapse(false)} fetchArticle={() => fetchArticle()} />
                <h1 className='text-center mb-2'>List of Articles</h1>
                <MaterialReactTable
                    columns={columns}
                    data={article}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    positionActionsColumn="last"
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            {(localStorage.getItem('role') == 'ar_staff2') ? '' :
                                <Tooltip arrow placement="left" title="Edit" >
                                    <IconButton aria-expanded={open} aria-controls="collapseID" onClick={() => {
                                        editArticle(row.id)
                                        setOpen(!open)

                                    }}>

                                        <Edit />
                                    </IconButton>
                                </Tooltip>
                            }
                            <Tooltip arrow placement="left" title="View" >
                                <IconButton onClick={() => {
                                    editArticle(row.id)
                                    setModalOpen(true);
                                    setArticleData(row.original)
                                }}>

                                    <Visibility />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Trash">
                                <IconButton style={{ color: "green" }} onClick={() => deleteArticle(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <>
                            {(localStorage.getItem('role') == 'ar_staff2') ? '' :
                                <Tooltip title="Add-Article">
                                    <IconButton aria-expanded={collapse} onClick={() => setCollapse(!collapse)}>
                                        Create <Add />
                                    </IconButton>
                                </Tooltip>
                            }
                        </>
                    )}

                />
                <ViewArticle
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    showArtGroup={showArtGroup}
                    showArtCategory={showArtCategory}
                    articleData={articleData}
                    fetchArticle={() => fetchArticle()}
                />
            </div>
        </IonContent>

    );

};

const ViewArticle: FC<{
    onClose: () => void;
    fetchArticle: () => void;

    open: boolean;
    articleData: any;
    showArtGroup: any;
    showArtCategory: any;

}> = (props) => {

    const approveArticle = () => {

        fetch(`${baseUrl}${api.approveArticle}${props.articleData.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }

        })

            .then(res => res.json())
            .then(res => {
                if (res) {
                    props.fetchArticle();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));
    }

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">View Article</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        //gap: '1.5rem',
                    }}
                >
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Article Status</h2>
                            <p>{
                                props.articleData.status === 'P' && 'Publish' ||
                                props.articleData.status === 'D' && 'Draft' ||
                                props.articleData.status === 'R' && 'Review'

                            }</p>
                        </IonLabel>
                    </IonItem>
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Article Title</h2>
                            <p>{props.articleData.title}</p>
                        </IonLabel>
                    </IonItem>
                    {props.showArtGroup.length > 0 ?
                        <IonItem >
                            <IonLabel>
                                <h2 className="mb-3">Article Groups</h2>
                                {props.showArtGroup.map((item: any, i: any) =>
                                    <p className="mt-3" key={i}> {item.name}</p>
                                )}
                            </IonLabel>
                        </IonItem> : ''
                    }
                    {props.showArtCategory.length > 0 ?
                        <IonItem >
                            <IonLabel>
                                <h2 className="mb-3">Article Category</h2>
                                {props.showArtCategory.map((item: any, i: any) =>
                                    <p className="mt-3" key={i}> {item.name}</p>
                                )}
                            </IonLabel>
                        </IonItem> : ''
                    }
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Article Content</h2>
                            <p dangerouslySetInnerHTML={{ __html: props.articleData.content }}></p>
                        </IonLabel>
                    </IonItem>
                    <IonItem>
                        <p>{props.articleData.date}</p>

                    </IonItem>
                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                {(localStorage.getItem('role') == 'ar_mgmt') &&
                    props.articleData.status === 'R'
                    ? <Button onClick={approveArticle}>Publish</Button>
                    : ''

                }
            </DialogActions>
        </Dialog>
    );
}
export default Articles;
