import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type questionData = {
    id: string;
    course_name: string;
    topic_name: string;
    level_name: string;
    title: string;
    question_type: string;
    answer: string;



};

//nested data is ok, see accessorKeys in ColumnDef below


const Question: FC = () => {
    const [questions, setQuestions] = useState([]);
    const [questionData, setQuestionData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);
    const fetchQuestions = () => {
        fetch(`${baseUrl}${api.allQuestion}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setQuestions(resp.reverse())
            })
    }
    useEffect(() => {
        fetchQuestions();
    }, [])

    const columns = useMemo<MRT_ColumnDef<questionData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
            },

            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },
            {
                accessorKey: 'title',
                header: 'Question',

            },

            {
                accessorKey: 'question_type',
                header: 'Question Type',

            },

            {
                accessorKey: 'answer',
                header: 'Question Answer',

            },

        ],
        [],
    );

    const deleteQuestion = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuestion}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchQuestions();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Questions</h1>
                <MaterialReactTable
                    columns={columns}
                    data={questions}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    setQuestionData(row.original)
                                    setModalOpen(true);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteQuestion(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />
                <EditTopic
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    fetchlevel={() => fetchQuestions()}
                    levelData={questionData}
                />
            </div>
        </IonContent>

    );

};

const EditTopic: FC<{
    onClose: () => void;
    fetchlevel: () => void;
    open: boolean;
    levelData: any

}> = (props) => {
    const [level_name, setLevel_name] = useState('');
    const [error, setError] = useState("");
    useEffect(() => {
        setLevel_name(props.levelData.level_name);
    }, [props.levelData])

    const updateLevel = () => {
        let data = {
            'level_name': level_name,
        }
        fetch(`${baseUrl}${api.updateLevel}${props.levelData.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update level Successfully');
                    props.fetchlevel();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">Update Level</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >

                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Lvel Name"
                        variant="filled"
                        focused
                        value={level_name || ''}
                        onChange={(e: any) => setLevel_name(e.target.value)} />

                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={updateLevel}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default Question;
