import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCard,
    IonAccordionGroup,
    IonAccordion,
    IonContent
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { useParams } from "react-router";
import baseUrl, { api } from '../../pages/Urls';
import TopicArticle from "./TopicArticle";
import swal from 'sweetalert';
import { useForm } from 'react-hook-form';
import { ErrorMessage } from '@hookform/error-message';
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const ViewTopic: React.FC<{
    courseId: string

}> = (props) => {
    type ViewTopic = {
        id: string;
    };
    const {
        register,
        handleSubmit,
        reset,
        formState,
        formState: { errors },
        setError,
    } = useForm();
    const [topic_name, setTopic_name] = useState('');
    const [topic_article, setTopic_article] = useState([]);
    const [topic_content, setTopic_content] = useState([]);
    const [tid, setId] = useState('');
    const [multipleFiles, setMultipleFiles] = useState([]);
    const { id } = useParams<ViewTopic>();
    const fetchViewTopic = () => {
        fetch(`${baseUrl}${api.showTopic}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setTopic_name(result.topic.topic_name);
                setTopic_article(result.topic.topic_article);
                setTopic_content(result.topic.topic_content);
                localStorage.setItem('topic_name', result.topic.topic_name);
                setId(result.topic.id)

            })
            .catch(function (error) {
                console.log(error);
            })
    }
    useEffect(() => {
        fetchViewTopic();
    }, [])

    const schema = yup.object().shape({
        file: yup.mixed()
            .test('required', "You need to provide a file", (multipleFiles: any) => {
                return multipleFiles && multipleFiles.length
            })
            .test("fileSize", "The file is too large", (value: any, context) => {
                for (const key of Object.keys(multipleFiles)) {
                    return value && value[key] && value[key].size <= 10240;
                }
            })
            .test("type", "We only support jpeg", function (value: any) {
                for (const key of Object.keys(multipleFiles)) {
                    return value && value[key] && value[key].type === "file/jpeg";
                }
            }),
    });
    const changeMultipleFiles = (e: any) => {
        if (e.target.files) {
            const fileArray: any = Array.from(e.target.files).map((file: any) =>
                URL.createObjectURL(file)
            );
            setMultipleFiles((prevFiles) => prevFiles.concat(fileArray));
        }
    };


    const onSubmit = (data: any) => {
        var myHeaders = new Headers();
        myHeaders.append("Authorization", `Bearer ${localStorage.getItem("token")}`);
        const formData = new FormData();
        for (const key of Object.keys(multipleFiles)) {
            formData.append('files[]', data.file[key]);
            if (
                !(data.file[key].type === "application/pdf")
                // !(data.file[key].type === "application/png")
                // data.file[key].type != "application/doc" ||
                // data.file[key].type != "application/docx" ||
                // data.file[key].type != "application/xlsx" ||
                // data.file[key].type != "application/zip" ||
                // data.file[key].type != "application/png" ||
                // data.file[key].type != "application/jpg" ||
                // data.file[key].type != "application/xls"

            ) {
                setError("file", {
                    type: "file",
                    message: "Invalid file !! only doc, docx, xlsx, xls, pdf, zip, png, jpg file are allowed"
                });
                return;
            }
        }
        formData.append('topic_name', topic_name)
        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: formData,

        };


        fetch(`${baseUrl}${api.addTopicContent}`, requestOptions)
            .then(response => response.json())
            .then(res => {
                if (res.status === true) {
                    fetchViewTopic();
                    setMultipleFiles([]);
                    reset({
                        file: ''
                    })
                    swal({
                        title: "Success!",
                        text: "File upload successfully",
                        icon: "success",
                    });
                }
            });





    };


    const updateTopic = () => {
        let data = {
            'topic_name': topic_name,

        }
        fetch(`${baseUrl}${api.updateTopic}${tid}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {

                    fetchViewTopic();
                    swal({
                        title: "Success!",
                        text: "Topic updated successfully",
                        icon: "success",
                    });

                }

            })
            .catch(error => console.log('error', error));


    }
    const downloadFile = (id: any, file: any) => {
        fetch(`${baseUrl}${api.downloadTopicContent}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            },

        }).then(response => {
            response.blob().then(blob => {
                let url = window.URL.createObjectURL(blob);
                let a = document.createElement('a');
                a.href = url;
                a.download = `${file}`;
                a.click();
            });

        });
    }

    const deleteTopicContent = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopicContent}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchViewTopic();
                        swal({
                            title: "Success!",
                            text: "File deleted successfully",
                            icon: "success",
                        });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <IonCard>
                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Update Topic</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">

                            <IonItem>
                                <IonLabel position="stacked">Topic Name</IonLabel>
                                <IonInput type="text"
                                    name="topic_name"
                                    value={topic_name}
                                    onIonChange={(e: any) => setTopic_name(e.target.value)}
                                    clearInput
                                    required
                                >
                                </IonInput>
                            </IonItem>
                            <hr />
                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="primary" onClick={updateTopic}>
                                    Save
                                </IonButton>

                            </div>

                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Topic Content</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            <form onSubmit={handleSubmit(onSubmit)}>
                                <input
                                    type="file"
                                    multiple
                                    {...register('file')}
                                    onChange={changeMultipleFiles}

                                />

                                <p style={{ color: "red" }}>
                                    <ErrorMessage errors={errors} name='file' />
                                </p>



                                <IonButton color="primary" type="submit">
                                    Save
                                </IonButton>
                            </form>
                            {topic_content.map((item: any, i: any) => (
                                <IonItem key={i.toString()}>
                                    <IonLabel > <button onClick={() => downloadFile(item.id, item.file)}>
                                        {item.file} <i className="bi bi-download"></i>
                                    </button></IonLabel>

                                    <button onClick={() => deleteTopicContent(item.id)}>
                                        <i className="bi bi-trash" title="delete"></i>
                                    </button>
                                </IonItem>
                            ))}
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Topic Article</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            <TopicArticle topicArticle={topic_article} fetchViewTopic={() => fetchViewTopic()} topic_name={topic_name} />

                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
        </IonContent>
    );
}


export default ViewTopic;
