import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';

import React, { useState, useEffect, useRef } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = (props) => {
    type ViewQuestionParams = {
        id: string;
    };
    const [questions, setQuestions] = useState([]);
    const [total_question, setTotalQuestion] = useState('');
    const [minutes, setMinutes] = useState(1);
    const [seconds, setSeconds] = useState(0);
    const [isAns, setIsAns]: any[] = useState([]);
    const [question_id, setQuestion_id] = useState('');
    //const [attempt_ans, setAttempt_ans] = useState('');
    const [duration, setDuration] = useState(20);
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();

    // const k = questions.map((q: any) => (q.id));
    // const values = isAns;
    // const keys = [...k]
    // const attempt = values.map((item: any) => Object.assign(
    //     keys.map(key => ({ [key]: item }))
    // ))


    //console.log(isAns)


    const idd: any = questions.map((q: any) => q.id);

    const ans = isAns.map((a: any) => (a));
    let attempt = {};


    attempt = { ...attempt, [idd[1]]: ans[1] }
    attempt = { ...attempt, [idd[2]]: ans[2] }
    //attempt = { ...attempt, [idd.map((d: any, i: any) => d[i])]: ans.map((a: any, i: any) => a) }
    console.log(attempt)


    // const idd2 = 5
    // const idd3 = 63
    // let eg = {}

    // eg = { ...eg, [idd2]: 'Hello' }
    // eg = { ...eg, [idd3]: 'World' }
    // console.log(eg)

    const submit = (e: any) => {

        if (window.confirm("want to submit?")) {

            e.preventDefault();
            let data = {
                'attempt_ans': attempt,
                //'question_id': question_id,

            }
            fetch(`${baseUrl}${api.attemptQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {

                        window.location.href = '/dashboard/quiz';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));


        }
    };
    const handleChange = (e: any, i: any) => {

        const inputdata = [...isAns]
        inputdata[i] = e.target.value;
        setIsAns(inputdata)
        console.log(e.target.value)

    };


    //console.log('att', attempt)

    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                //setQuestion_id(result.map((op: any) => (op.id)))
                setTotalQuestion(result.length);

            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])
    //console.log('Q', isAns);

    useEffect(() => {
        let myInterval = setInterval(() => {
            if (seconds > 0) {
                setSeconds(seconds - 1);
            }
            if (seconds === 0) {
                if (minutes === 0) {
                    clearInterval(myInterval)
                } else {
                    setMinutes(minutes - 1);
                    setSeconds(59);
                }
            }
        }, 1000)
        return () => {
            clearInterval(myInterval);
        };
    });
    //console.log('op', question_id)

    return (

        <>
            <IonCard>
                <IonCardHeader>
                    <IonCardTitle>
                        <IonGrid>
                            <IonRow>
                                <IonCol>Total Question -- {total_question}</IonCol>
                                <IonCol>
                                    {minutes === 0 && seconds === 0
                                        ? ''
                                        : <h1> {minutes < 10 ? `0${minutes}` : minutes}:{seconds < 10 ? `0${seconds}` : seconds}</h1>
                                    }
                                </IonCol>
                            </IonRow>
                        </IonGrid>
                    </IonCardTitle>
                </IonCardHeader>

                <IonCardContent>

                    {questions.map((qstn: any, i: any) =>

                        <IonCard key={i.toString()}>
                            <IonCardHeader>
                                <IonCardTitle>
                                    {/* <div className='mb-2'>
                                        {++i} / {total_question}
                                    </div> */}

                                    {qstn.question}
                                    <input type='text'
                                        name='question_id'
                                        value={qstn.id}
                                        onChange={(e) => setQuestion_id(e.target.value)}
                                    ></input>
                                    <>
                                        {
                                            (qstn.question_type != 'MCQ') ?
                                                <>
                                                    <IonItem>
                                                        <IonInput name='attempt_ans' value={isAns[i]} placeholder="enter answer" onIonChange={(e) => handleChange(e, i)}></IonInput>

                                                    </IonItem>
                                                </> :
                                                <>
                                                    <IonList>

                                                        <IonRadioGroup name='attempt_ans' value={isAns[i]} onIonChange={(e) => handleChange(e, i)} >
                                                            {qstn.option.map((op: any, i: any) => {
                                                                return (

                                                                    <IonItem key={i}>
                                                                        <IonLabel >{op.option}</IonLabel>
                                                                        <IonRadio slot="start" value={op.option} ></IonRadio>

                                                                    </IonItem>

                                                                );
                                                            })}
                                                        </IonRadioGroup>
                                                    </IonList>
                                                </>
                                        }


                                    </>
                                </IonCardTitle>
                            </IonCardHeader>
                        </IonCard>


                    )}

                    <div className='text-center mt-3 mb-5'>
                        <IonButton fill="outline" onClick={(e) => submit(e)}>submit</IonButton>
                    </div>
                </IonCardContent>

            </IonCard>



        </>

    );
}
export default ViewQuestion;
