import {
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonContent,
    IonMenu,
    IonList,
    IonItem,
    IonRouterOutlet,
    IonMenuButton,
    IonButtons
} from '@ionic/react';
import React, { Component } from "react";
import { Redirect } from 'react-router-dom';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import Articles from '../components/articles/Articles';
import Group from './Group';





class AdminLayout extends Component<any, any>{
    constructor(props: any) {
        super(props);

        this.state = {

            redirectToReferrer: false,
            token: null,

        };
        this.logout = this.logout.bind(this);

    }


    componentWillMount() {

        if (localStorage.getItem('token')) {
            this.setState({
                token: localStorage.getItem("token")
            });

        }

        else {
            this.setState({ redirectToReferrer: true });
        }


    }

    logout() {
        localStorage.setItem('token', '');
        localStorage.clear();
        this.setState({ redirectToReferrer: true });
    }


    render() {
        if (this.state.redirectToReferrer) {
            return (<Redirect to={'/login'} />)
        }
        const routes = [
            {
                path: "/",
                exact: true,

                main: () => <Articles />
            },
            {
                path: "/group",

                main: () => <Group />
            },

        ];

        return (
            <IonPage>
                <IonHeader>
                    <IonToolbar>
                        <IonButtons slot="start">
                            <IonMenuButton menu="first"></IonMenuButton>
                        </IonButtons>

                        <IonTitle>Dashboard</IonTitle>

                    </IonToolbar>
                </IonHeader>
                <IonMenu side="start" menuId="first" contentId="main">
                    <IonHeader>
                        <IonToolbar color="medium">
                            <IonTitle>Dashboard</IonTitle>
                        </IonToolbar>
                    </IonHeader>
                    <IonContent>
                        <IonList>
                            <IonItem> <Link to="/">Manage Article</Link></IonItem>
                            <IonItem> <Link to="/group">Manage Group</Link></IonItem>
                            <IonItem>Menu Item</IonItem>
                            <IonItem onClick={(e: any) => this.logout()}>logout</IonItem>
                        </IonList>
                    </IonContent>
                </IonMenu>
                <IonRouterOutlet id="main"></IonRouterOutlet>

                <IonContent>

                </IonContent>

            </IonPage>
        );
    }
}
export default AdminLayout;
