import {
    IonContent,
    IonSearchbar,

} from '@ionic/react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import '../../../pages/Style.css';
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory } from "react-router";
import { Link } from 'react-router-dom';

const Blog: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [search, setSearch] = useState('');
    const history = useHistory();

    const fetchAuthBlog = () => {
        fetch(`${baseUrl}${api.authBlog}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }
    useEffect(() => {
        fetchAuthBlog();
    }, [])



    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - article.length) : 0;

    //console.log("arr", article);
    return (
        <IonContent>
            <div className="container mt-3">
                <h2>Recent Articles Post</h2>
                <hr />
                <IonSearchbar onIonChange={(e: any) => setSearch(e.target.value)}></IonSearchbar>
                <Paper sx={{ width: '100%', mb: 2, mt: 3 }}>

                    <TableContainer>
                        <Table aria-label="simple table">

                            <TableBody>
                                {(rowsPerPage > 0
                                    ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : article
                                ).filter((item: any) => {
                                    return search.toLowerCase() === '' ? item :
                                        item.title.toLowerCase().includes(search);

                                }).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell >
                                            <div>
                                                <h4 className="text-primary"><Link className='text-decoration-none' to={`/dashboard/blog/${item.id}`}>{item.title}</Link></h4>
                                            </div>
                                            <div>
                                                {item.date}
                                            </div>

                                        </TableCell>


                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[10, 15, 20]}
                        component="div"
                        count={article.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>
            </div>
        </IonContent>

    );
}


export default Blog;
