import {
    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,
    IonItem,
    IonContent,
    IonCol, IonGrid, IonRow

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import baseUrl, { api } from '../../../pages/Urls';
import { format } from 'date-fns';
import "./ViewBlog.css";


const ViewBlog: React.FC = () => {
    type ViewBlogParams = {
        slug: string;
    };
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState(format(new Date(), 'yyyy-MM-dd'));
    const [category, setCategory] = useState([]);

    const history = useHistory();
    const { slug } = useParams<ViewBlogParams>();

    const viewArticle = () => {
        fetch(`${baseUrl}${api.viewArticle}${slug}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
                setCategory(result.category);
            })
    }
    useEffect(() => {
        viewArticle();

    }, [])

    return (
        <IonContent>
            <IonGrid>
                <IonRow>
                    <IonCol>
                        <IonCard>
                            <div className="container mt-3 mb-5">
                                <IonCardHeader>
                                    <IonToolbar>

                                        <IonButtons slot="start">
                                            <IonButton onClick={() => history.goBack()} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                        </IonButtons>
                                    </IonToolbar>
                                </IonCardHeader>


                                <IonCardContent>
                                    <IonItem>
                                        <h1 style={{
                                            fontWeight: 'bold',
                                            fontSize: '2rem',
                                        }}>{title}</h1>
                                    </IonItem>
                                    <IonItem>
                                        <h4>{format(new Date(date), 'MMMM dd, yyyy')}</h4>
                                    </IonItem>
                                    <IonItem>
                                        <div
                                            id="article-content"
                                            dangerouslySetInnerHTML={{ __html: content }}
                                        />
                                    </IonItem>
                                    <IonItem>
                                        <IonGrid>
                                            <IonRow>
                                                <IonCol> </IonCol>
                                                <IonCol>{category?.map((item: any, i: any) => {
                                                    return <a key={i} className='text-decoration-none'>{(i ? ', ' : '') + item.name}</a>;
                                                })}</IonCol>

                                            </IonRow>
                                        </IonGrid>

                                    </IonItem>
                                </IonCardContent>


                            </div>
                        </IonCard>
                    </IonCol>

                    <IonCol size="auto">

                        <div style={{ width: "200px" }}>
                            <h3>Categories</h3>


                        </div>

                    </IonCol>

                </IonRow>
            </IonGrid>


        </IonContent>

    );
};
export default ViewBlog;
