import {

    IonPage,
    IonContent,
    IonTitle,
    IonSegment,
    IonSegmentButton,
    IonRouterOutlet,
    IonSplitPane,
    IonHeader,
    IonToolbar,
    IonButtons,
    IonMenuButton

} from "@ionic/react";
import { Redirect } from 'react-router-dom';
import { Route, useHistory, useLocation } from "react-router";

import { IonReactRouter } from '@ionic/react-router';
import AdminNav from '../components/nav/AdminNav';
import Articles from './admin/articles/Articles';
import Group from './admin/Group';
import Course from './admin/quiz/Course';
import Topic from './admin/quiz/Topic';
import Quiz from './admin/quiz/Quiz';
import Level from './admin/quiz/Level';
import Question from './admin/quiz/Question';
import TrashArticle from './admin/articles/TrashArticle';
import TeacherNav from '../components/nav/TeacherNav';
import CourseList from './teacher/CourseList';
import MyCourse from './teacher/MyCourse';
import ViewCourse from './teacher/ViewCourse';
import UserNav from '../components/nav/UserNav';
import Blog from './user/blog/Blog';
import ViewBlog from './user/blog/ViewBlog';
import Uquiz from './user/uquiz/Uquiz';
import ViewQuestion from './user/uquiz/ViewQuestion';
import QuizResult from './user/uquiz/QuizResult';
import Menu from '../components/Menu';




const Dashboard: React.FC = () => {
    const admin = (localStorage.getItem('role') == 'ar_mgmt' ||
        localStorage.getItem('role') == 'ar_admin' ||
        localStorage.getItem('role') == 'ar_staff1' ||
        localStorage.getItem('role') == 'ar_staff2');
    const teacher = (localStorage.getItem('role') == 'ar_teacher');
    const user = (localStorage.getItem('role') == '');


    return (
        <IonPage>

            {/* {
                (localStorage.getItem('role') == 'ar_mgmt' ||
                    localStorage.getItem('role') == 'ar_admin' ||
                    localStorage.getItem('role') == 'ar_staff1' ||
                    localStorage.getItem('role') == 'ar_staff2') ? (
                    <Route path="/dashboard" component={AdminDashboard} />
                ) :
                    <Route path="/dashboard" component={TeacherDashboard} />


            } */}

            {admin && (
                <Route path="/dashboard" component={AdminDashboard} />
            )
            }
            {teacher && (
                <Route path="/dashboard" component={TeacherDashboard} />
            )
            }
            {user && (
                <Route path="/dashboard" component={UserDashboard} />
            )
            }

        </IonPage>
    );
};

const AdminDashboard: React.FC = () => {
    const history = useHistory()
    const pathname = history.location.pathname
    return (



        <IonContent>

            <AdminNav pathname={pathname} />

            <Route path="/dashboard/article" component={Articles} />
            <Route path="/dashboard/group" component={Group} />
            <Route path="/dashboard/course" component={Course} />
            <Route path="/dashboard/topic" component={Topic} />
            <Route path="/dashboard/quiz" component={Quiz} />
            <Route path="/dashboard/level" component={Level} />
            <Route path="/dashboard/question" component={Question} />
            <Route path="/dashboard/trash" component={TrashArticle} />
            <Route exact path="/dashboard">
                <Redirect to="/dashboard/article" />
            </Route>

        </IonContent>


    );
};
const TeacherDashboard: React.FC = () => {
    const history = useHistory()
    const pathname = history.location.pathname
    return (

        <IonSplitPane when='sm' contentId="main-content">
            <Menu />
            <IonPage id="main-content">
                <TemporaryHeader />
                <IonContent className="ion-padding">
                    <IonRouterOutlet id="main-content">
                        <Route path="/dashboard/course/list" exact>
                            <CourseList />
                        </Route>
                        <Route path="/dashboard/mycourse" exact>
                            <MyCourse />

                        </Route>

                        <Route path="/dashboard/mycourse/:id" >
                            <ViewCourse />

                        </Route>
                        {/* Default Redirect */}
                        <Route path="/dashboard" exact>
                            <Redirect to="/dashboard/course/list" />
                        </Route>
                    </IonRouterOutlet>
                </IonContent>
            </IonPage>
        </IonSplitPane>
    );
};
const TemporaryHeader: React.FC = () => {
    const location = useLocation();
    const title = () => {
        if (location.pathname === "/dashboard/course/list") return "Course";
        if (location.pathname === "/dashboard/mycourse") return "My Course";




    };
    return (
        <IonHeader>
            <IonToolbar>
                <IonButtons slot="start">
                    <IonMenuButton></IonMenuButton>
                </IonButtons>
                <IonTitle>{title()}</IonTitle>
            </IonToolbar>
        </IonHeader>
    );
};
const UserDashboard: React.FC = () => {
    const history = useHistory()
    const pathname = history.location.pathname;

    return (
        <>

            <UserNav pathname={pathname} />
            <IonContent className="ion-padding">
                <IonRouterOutlet>
                    <Route path="/dashboard/blog" component={Blog} />
                    <Route path="/dashboard/view/blog/:id" component={ViewBlog} />
                    <Route path="/dashboard/quiz">
                        <UserQuiz />
                    </Route>

                    <Route exact path="/dashboard">
                        <Redirect to="/dashboard/blog" />
                    </Route>

                </IonRouterOutlet>
            </IonContent>
        </>

        // <IonPage>
        //     <IonTitle>user dashboard</IonTitle>
        // </IonPage>

    );
};
const UserQuiz: React.FC = () => {


    return (
        <>

            <IonRouterOutlet>
                <IonContent>
                    <Route exact path="/dashboard/quiz"  >
                        <Uquiz />
                    </Route>
                    <Route exact path="/dashboard/quiz/:id" >
                        <ViewQuestion />
                    </Route>
                    <Route exact path="/dashboard/quiz/result/:id">
                        <QuizResult />
                    </Route>
                </IonContent>
            </IonRouterOutlet>
        </>



    );
};

export default Dashboard;
