import {
    IonContent,
    IonItem,
    IonButton,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';
import React, { useState, useEffect, useMemo } from 'react';
import { useHistory, useParams, useLocation } from 'react-router-dom';
import { format } from 'date-fns'
import Countdown from "react-countdown";
import baseUrl, { api } from '../../Urls';
import '../../../pages/Style.css';
import ReactPaginate from "react-paginate";


const useCountdown = (deadline: Date) => {
    // Time is in seconds
    const [time, setTime] = useState(
        Math.max(0, Math.floor((deadline.getTime() - Date.now()) / 1000))
    );

    const decrement = () =>
        setTime((prevTime) => {
            return prevTime === 0 ? 0 : prevTime - 1;
        });

    useEffect(() => {
        const ids = setInterval(decrement, 1000);
        return () => clearInterval(ids);
    }, []);

    const format = (num: number): string => {
        return num < 10 ? '0' + num : num.toString();
    };

    return {
        //days: format(Math.floor(time / (3600 * 24))),
        hours: format(Math.floor((time / 3600) % 24)),
        minutes: format(Math.floor((time / 60) % 60)),
        seconds: format(time % 60),
    };
};
const QuizQuestion: React.FC<{
    courseId: string,

}> = (props) => {
    type QuizQuestion = {
        id: string;
    };
    const { id } = useParams<QuizQuestion>();
    const history = useHistory();
    const [question, setQuestion] = useState([]);
    const [isAns, setIsAns]: any[] = useState([]);
    const [test_end, setTest_end] = useState(format(new Date(), 'yyyy-MM-dd HH:mm:ss'));
    const [test_start, setTest_start] = useState('');
    const [quizId, setQuizId] = useState('');
    const [isAttempt, setIsAttempt] = useState('');
    const [time22, setTime] = React.useState(format(new Date(), 'HH:mm:ss'));
    // const [pageNumber, setPageNumber] = useState(0);
    // const questionPerPage = 3;
    // const pagesVisited = pageNumber * questionPerPage;
    // const pageCount = Math.ceil(question.length / questionPerPage);

    useEffect(() => {
        const timer22ID = setInterval(() => {
            tick()
            if (time22 === format(new Date(test_end), 'HH:mm:ss')) {
                submit()
            }

        }, 1000);
        return function cleanup() {
            clearInterval(timer22ID);
        };
    });

    function tick() {
        setTime(format(new Date(), 'HH:mm:ss'));

    }

    // useEffect(() => {
    //     return history.listen(() => { // listen
    //         if (history.action === "POP") {
    //             history.replace(`/dashboard/mycourse/${props.courseId}/quiz/${localStorage.getItem("quiz_id")}`);
    //         }
    //     });
    // }, [history]);


    ////

    useEffect(() => {
        setTest_start(format(new Date(), 'yyyy-MM-dd HH:mm:ss'))

        fetch(`${baseUrl}${api.viewQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setQuestion(result.question)
                setTest_end(result.question[0].test_end)
                setQuizId(result.question.map((item: any) => (item.quiz_id)))
                setIsAttempt(result.quiz.map((item: any) => (item.is_attempt)))
                //localStorage.setItem('quiz_id', result.question[0].quiz_id)
                //localStorage.setItem('test_end', result.question[0].test_end)
                //history.replace(`/dashboard/mycourse/${props.courseId}/quiz/${localStorage.getItem("quiz_id")}`)
            })


    }, []);

    console.log(question)
    let attempt = {};
    question.map((q: any, i) => {
        attempt = { ...attempt, [q.id]: isAns[i] }
    })
    const handleChange = (e: any, i: any) => {
        const inputdata = [...isAns]

        inputdata[i] = e.target.value;
        setIsAns(inputdata)
        //console.log(inputdata)

    };

    const submit = () => {

        let data = {

            'attemptans': attempt,
            'quiz_id': quizId,

        }

        fetch(`${baseUrl}${api.quizAttempt}`, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(response => {
                if (response.status === 200) {
                    window.confirm('Want to enrole this course?')
                    window.location.href = `/dashboard/mycourse/${props.courseId}/quiz/result`;

                }
                else {
                    throw Error([response.status, response.statusText].join(' '));
                }
            })

            .catch(error => console.log('error', error));




    };


    // const changePage = ({ selected: selectedPage }: any) => {
    //     setPageNumber(selectedPage);
    // };

    ///////////////////////////
    // useEffect(() => {
    //     const tm1 = new Date(test_end).getTime();
    //     const tm2 = new Date(test_start).getTime();
    //     const ddd = tm1 - tm2;

    //     console.log(ddd)

    // }, [test_end, test_start]);

    ///////////////////////////////////
    const st = format(new Date(), 'yyyy-MM-dd HH:mm:ss');
    const etm: any = localStorage.getItem("test_end");
    const et = format(new Date(etm), 'yyyy-MM-dd HH:mm:ss');
    const end = new Date(et).getTime();
    const start = new Date(st).getTime();
    const ddd = end - start;
    // console.log('ts', start)
    // console.log('te', end)
    // console.log('ddd', ddd)
    //console.log(typeof (test_start))
    //console.log(question)

    const [deadline] = useState(new Date(Date.now() + ddd));

    ////////////////////////////////////////////////////////////////////////////

    // const [deadline, setDdeadline] = useState(new Date(Date.now()));
    // useEffect(() => {
    //     const tm1 = new Date(test_end).getTime();
    //     const tm2 = new Date(test_start).getTime();
    //     const ddd = tm1 - tm2;
    //     setDdeadline(new Date(Date.now() + ddd))
    // }, [test_end, test_start])
    // console.log('dead', deadline)
    //const [deadline] = useState(new Date(Date.now() + 12022));

    ///////////////////////////////////////////////////////////////////////////////

    const timer = useCountdown(deadline);
    //console.log('timer', timer)
    return (

        <>

            <IonContent>

                {isAttempt != '1' ?

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>

                                <div className='text-center mt-3'>Total Question -- {question.length} & Quiz will finish {test_end}</div>
                                <div className='text-center'>{time22}</div>


                                <div className='mt-3 mb-3'> {timer.hours}:{timer.minutes}:{timer.seconds}</div>

                                {/* end: {new Date(test_end).getTime()}
                                <br></br>
                                start: {new Date(test_start).getTime()}
                                <br></br> */}
                                {/* time: {new Date(ddd).getTime()} */}
                            </IonCardTitle>
                        </IonCardHeader>

                        <IonCardContent>

                            {question
                                .map((qstn: any, i: any) =>

                                    <IonCard key={i.toString()}>
                                        <IonCardHeader>
                                            <IonCardTitle>
                                                <div className='mb-2'>
                                                    {i + 1} / {question.length} -- {qstn.title}
                                                </div>


                                                <h2 dangerouslySetInnerHTML={{ __html: qstn.q_content }}></h2>

                                                <>
                                                    {
                                                        (qstn.question_type != 'MCQ') ?
                                                            <>
                                                                <IonItem>
                                                                    <IonInput name='attemptans'
                                                                        value={isAns[i]}
                                                                        placeholder="enter answer"
                                                                        onIonChange={(e) => handleChange(e, i)}>

                                                                    </IonInput>

                                                                </IonItem>
                                                            </> :
                                                            <>
                                                                <IonList>

                                                                    <IonRadioGroup name='attemptans'
                                                                        //selected={isAns[pagesVisited + i]}
                                                                        value={isAns[i]}
                                                                        onIonChange={(e) => handleChange(e, i)} >
                                                                        {qstn.option.map((op: any, index: any) => {
                                                                            //console.log(isAns[pagesVisited + i])
                                                                            return (

                                                                                <IonItem key={index}>
                                                                                    <IonLabel >{op.option}</IonLabel>
                                                                                    <IonRadio slot="start" value={op.option} >
                                                                                    </IonRadio>

                                                                                </IonItem>

                                                                            );
                                                                        })}
                                                                    </IonRadioGroup>
                                                                </IonList>
                                                            </>
                                                    }


                                                </>
                                            </IonCardTitle>
                                        </IonCardHeader>
                                    </IonCard>


                                )}

                            <div className='text-center mt-3 mb-5'>
                                <IonButton id="quiz_submit" fill="outline" onClick={submit}>submit</IonButton>
                            </div>



                        </IonCardContent>

                    </IonCard>


                    :
                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <div className='text-center mt-3 mb-5'>
                                    You Already Finished your Quiz
                                </div>
                            </IonCardTitle>
                        </IonCardHeader>

                        <IonCardContent>
                            <div className='text-center mt-3 mb-5'>
                                <IonButton fill="outline" href={`/dashboard/mycourse/${props.courseId}`}>Back to Course</IonButton>
                            </div>
                        </IonCardContent>
                    </IonCard>
                }

            </IonContent>
        </>
    );
};


export default QuizQuestion;
