import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';
import baseUrl, { api } from '../../pages/Urls';
const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};
const QuizFrom: React.FC<{
    open: boolean
    handleClose: any
    course_name: string
    courseId: string
}> = (props) => {

    const [quiz_name, setQuiz_name] = useState('');
    const [level, setLevel] = useState([]);
    const [level_names, setLevel_names] = useState('');
    const [status, setStatus] = useState('');
    const addQuiz = (e: any) => {
        {
            let data = {
                'course_name': props.course_name,
                'quiz_name': quiz_name,

            }
            fetch(`${baseUrl}${api.addQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Quiz created Successfully');
                        window.location.href = `/dashboard/mycourse/${props.courseId}/quiz`;

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <div>

            <Modal
                open={props.open}
                onClose={props.handleClose}
                aria-labelledby="modal-modal-title"

            >
                <Box sx={style}>
                    <div className="text-end">
                        <Button onClick={props.handleClose} variant="text">X</Button>
                    </div>
                    <Typography sx={{ mt: 3 }} id="modal-modal-title" variant="h6" component="h2">
                        Add Quiz
                    </Typography>
                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Topic Name"
                        variant="filled"
                        focused
                        onChange={(e: any) => setQuiz_name(e.target.value)} />
                    <TextField sx={{ mt: 3 }}
                        type="file"
                        label="Topic File"
                        variant="filled"
                        focused />

                    <div>
                        <Button sx={{ mt: 3 }} variant="outlined" onClick={addQuiz}>Add</Button>
                    </div>
                </Box>
            </Modal>
        </div>
    );
}
export default QuizFrom;
