import React, { FC, useMemo, useState, useEffect } from 'react';
import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,

} from "@ionic/react";
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    IconButton,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import AddCourse from './AddCourse';
import { useForm } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
type CourseData = {
    id: string;
    course_name: string;
    description: string;

};


const Course: FC = () => {
    const [course, setCourse] = useState([]);
    const [instructor, setInstructor] = useState([]);
    const [course_name, setCourse_name] = useState('');
    const [description, setDescription] = useState('');
    const [id, setId] = useState('');
    const [crs_inst, setCrs_inst] = useState([]);
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState: { errors },
        reset,

    } = useForm();
    useEffect(() => {


        reset({
            course_name: course_name,
            description: description,
            crs_inst: crs_inst
        })

    }, []);
    const fetchCourse = () => {
        fetch(`${baseUrl}${api.allCourse}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }
    const fetchInstructor = () => {
        fetch(`${baseUrl}${api.allInstructor}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setInstructor(resp)
            })
    }
    useEffect(() => {
        fetchCourse();
        fetchInstructor();
    }, [])

    const columns = useMemo<MRT_ColumnDef<CourseData>[]>(
        () => [

            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'description',
                header: 'Description',

            },

        ],
        [],
    );
    const editCourse = (id: any) => {

        fetch(`${baseUrl}${api.editCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setDescription(result.course.description)
                setId(result.course.id);

                setCrs_inst(

                    result.course.instructor.map((ins: any) => (ins.name))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateCourse = (e: any) => {

        fetch(`${baseUrl}${api.updateCourse}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {

                    fetchCourse();
                    setOpen(false)

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteCourse = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteCourse}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        fetchCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }

    return (
        <IonContent>
            <div className="container mt-5">
                <Collapse in={open}>
                    <div id="collapseID">
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Course</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <form onSubmit={handleSubmit(updateCourse)}>
                                        <IonItem>
                                            <IonLabel position="stacked">Course Name</IonLabel>
                                            <IonInput
                                                placeholder="Course Name"
                                                type="text"
                                                {...register("course_name", { required: "Course Name Is Required" })}
                                            ></IonInput>

                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name="course_name" />
                                        </p>

                                        <IonItem>
                                            <IonLabel position="stacked">Description</IonLabel>
                                            <IonInput type="text"
                                                placeholder="Course description"

                                                {...register("description")}
                                            >
                                            </IonInput>
                                        </IonItem>

                                        <IonItem >
                                            <IonLabel position="stacked">Course Instructor</IonLabel>
                                            <IonSelect

                                                {...register("crs_inst", { required: "Course Instructor Is Required" })}

                                                placeholder="Select instructors" multiple={true}>
                                                {instructor.map((item: any, i: any) => (
                                                    < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                                ))}
                                            </IonSelect>

                                        </IonItem>
                                        <p style={{ color: "red" }}>
                                            <ErrorMessage errors={errors} name="crs_inst" /></p>

                                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                            <IonButton color="danger" onClick={() => setOpen(false)}>
                                                Cancel
                                            </IonButton>
                                            <IonButton color="primary" type="submit">
                                                Save
                                            </IonButton>

                                        </div>
                                    </form>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </div>
                </Collapse>
                <AddCourse collapse={collapse} instructor={instructor} onClose={() => setCollapse(false)} fetchCourse={() => fetchCourse()} />
                <h1 className='text-center mb-2'>List of Courses</h1>
                <MaterialReactTable
                    columns={columns}
                    data={course}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    positionActionsColumn="last"
                    enableRowNumbers
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    editCourse(row.id)
                                    setOpen(!open)
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteCourse(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-Course">
                            <IconButton aria-expanded={collapse} onClick={() => setCollapse(!collapse)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />

            </div>
        </IonContent>

    );

};



export default Course;
