import {
    IonContent,
    IonItem,
    IonButton,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';
import React, { useState, useEffect, useMemo } from 'react';
import { useHistory, useParams, useLocation } from 'react-router-dom';
import { format } from 'date-fns'
import baseUrl, { api } from '../../Urls';
import '../../../pages/Style.css';
import ReactPaginate from "react-paginate";


const QuizQuestion: React.FC<{
    courseId: string,

}> = (props) => {
    type QuizQuestion = {
        id: string;
    };
    const { id } = useParams<QuizQuestion>();
    const history = useHistory();
    const [question, setQuestion] = useState([]);
    const [isAns, setIsAns]: any[] = useState([]);
    const [test_end, setTest_end] = useState(format(new Date(), 'yyyy-MM-dd HH:mm:ss'));
    const [quizId, setQuizId] = useState('');
    const [isAttempt, setIsAttempt] = useState('');
    ///////////////////////////////////////////
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - question.length) : 0;
    //const [selected, setSelected] = useState(0);
    const [pageNumber, setPageNumber] = useState(0);
    const questionPerPage = 2;
    const pagesVisited = pageNumber * questionPerPage;
    const pageCount = Math.ceil(question.length / questionPerPage);

    //console.log('pg', pageNumber)
    //console.log('pv', pagesVisited)



    ////
    useEffect(() => {
        return history.listen(() => { // listen
            if (history.action === "POP") {
                history.replace(`/dashboard/mycourse/${localStorage.getItem("course_id")}/quiz/${localStorage.getItem("quiz_id")}`);
            }
        });
    }, [history]);

    useEffect(() => {


        fetch(`${baseUrl}${api.viewQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setQuestion(result.question)
                setTest_end(result.question[0].test_end)
                setQuizId(result.question.map((item: any) => (item.quiz_id)))
                setIsAttempt(result.quiz.map((item: any) => (item.is_attempt)))
                //localStorage.setItem('quiz_id', result.question[0].quiz_id)
                //localStorage.setItem('test_end', result.question[0].test_end)
                //history.replace(`/dashboard/mycourse/${props.courseId}/quiz/${localStorage.getItem("quiz_id")}`)
            })


    }, []);

    //console.log(question)
    let attempt = {};
    question.map((q: any, i) => {
        attempt = { ...attempt, [q.id]: isAns[i] }
    })
    const handleChange = (e: any, i: any) => {
        const inputdata = [...isAns];
        inputdata[pagesVisited + i] = e.target.value;
        console.log('ans', inputdata)
        setIsAns(inputdata)

        //console.log('page No', [pagesVisited + i])


    };
    //console.log('ans', attempt)

    const submit = () => {
        if (quizId && attempt) {
            let data = {

                'attemptans': attempt,
                'quiz_id': quizId,

            }

            fetch(`${baseUrl}${api.quizAttempt}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        //window.confirm('Want to submit?')
                        window.location.href = `/dashboard/mycourse/${props.courseId}/quiz/result`;

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));


        }

    };


    const changePage = ({ selected }: any) => {
        setPageNumber(selected);
    };

    ///////////////////////////

    const st = format(new Date(), 'yyyy-MM-dd HH:mm:ss');
    const etm: any = localStorage.getItem("test_end");
    const et = format(new Date(etm), 'yyyy-MM-dd HH:mm:ss');
    const end = new Date(et).getTime();
    const start = new Date(st).getTime();
    const ddd = end - start;

    const [deadline] = useState(new Date(Date.now() + ddd));

    const useCountdown = (deadline: Date) => {
        // Time is in seconds
        const [time, setTime] = useState(
            Math.max(0, Math.floor((deadline.getTime() - Date.now()) / 1000))
        );

        const decrement = () =>
            setTime((prevTime) => {
                return prevTime === 0 ? 0 : prevTime - 1;
            });

        useEffect(() => {
            //const ids = setInterval(decrement, 1000);
            const ids = setInterval(() => {
                decrement()
                if (time == 0) {
                    submit()
                }

            }, 1000);
            return () => clearInterval(ids);
        }, [time]);

        const format = (num: number): string => {
            return num < 10 ? '0' + num : num.toString();
        };

        return {
            //days: format(Math.floor(time / (3600 * 24))),
            hours: format(Math.floor((time / 3600) % 24)),
            minutes: format(Math.floor((time / 60) % 60)),
            seconds: format(time % 60),
        };
    };

    const timer = useCountdown(deadline);

    return (

        <>

            <IonContent>

                {isAttempt != '1' ?

                    <Paper sx={{ width: '100%', mb: 2 }}>
                        <p className="h1 text-center">My Courses</p>
                        <div className="input-group rounded">
                            <input type="search" className="form-control rounded mx-2" placeholder="Search" onChange={(e: any) => setSearch(e.target.value)} />

                        </div>
                        <TableContainer>
                            <Table aria-label="simple table">
                                <TableHead>
                                    <TableRow>

                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {(rowsPerPage > 0
                                        ? mycourse.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                        : mycourse
                                    ).filter((item: any) => {
                                        return search.toLowerCase() === '' ? item :
                                            item.course_name.toLowerCase().includes(search);

                                    }).map((item: any, i: any) => (
                                        <TableRow key={i.toString()}>
                                            <TableCell >
                                                <IonCard>
                                                    <IonCardHeader>
                                                        {i + page * rowsPerPage}
                                                        <IonCardTitle>
                                                            <Link className='text-decoration-none' to={`/dashboard/mycourse/${item.course_id}`}> {item.course_name}</Link>
                                                        </IonCardTitle>

                                                    </IonCardHeader>

                                                    <IonCardContent>
                                                        {item.description}
                                                    </IonCardContent>

                                                </IonCard>

                                            </TableCell>

                                        </TableRow>
                                    ))}


                                </TableBody>
                            </Table>

                        </TableContainer>
                        <TablePagination
                            rowsPerPageOptions={[]}
                            component="div"
                            count={mycourse.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </Paper>


                    :
                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <div className='text-center mt-3 mb-5'>
                                    You Already Finished your Quiz
                                </div>
                            </IonCardTitle>
                        </IonCardHeader>

                        <IonCardContent>
                            <div className='text-center mt-3 mb-5'>
                                <IonButton fill="outline" href={`/dashboard/mycourse/${props.courseId}`}>Back to Course</IonButton>
                            </div>
                        </IonCardContent>
                    </IonCard>
                }

            </IonContent>
        </>
    );
};


export default QuizQuestion;
